void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;
    FILE *log = NULL;

    /* Minimal probe: record entry and initial state */
    log = fopen("/tmp/logs/2955.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();
    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: initial ArgC=%u\n", (unsigned)ArgC);
    }

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();
        if (log != NULL)
        {
            fprintf(log, "UT_BSP_Setup: ArgV=%p (pointer to first arg)\n", (void *)ArgV);
        }

        while (ArgC > 0)
        {
            --ArgC;

            if (log != NULL)
            {
                fprintf(log, "UT_BSP_Setup: loop start, remaining ArgC=%u, current ArgV[0]=%p ('%s')\n",
                        (unsigned)ArgC, (void *)ArgV[0], ArgV[0] ? ArgV[0] : "(null)");
            }

            if (strcmp(ArgV[0], "-d") == 0)
            {
                if (log != NULL) fprintf(log, "UT_BSP_Setup: matched '-d' -> set debug\n");
                UserShift = UTASSERT_CASETYPE_DEBUG;
            }
            if (strcmp(ArgV[0], "-q") == 0)
            {
                if (log != NULL) fprintf(log, "UT_BSP_Setup: matched '-q' -> set failure\n");
                UserShift = UTASSERT_CASETYPE_FAILURE;
            }
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                /* We will consume one extra argument (the verbose level). Log the parameter before use. */
                --ArgC;
                if (log != NULL)
                {
                    const char *param = ArgV[1];
                    fprintf(log, "UT_BSP_Setup: matched '-v', param pointer ArgV[1]=%p, contents='%s'\n",
                            (void *)ArgV[1], param ? param : "(null)");
                }

                /* Parse verbosity parameter (original behavior preserved) */
                {
                    unsigned long parsed = strtoul(ArgV[1], NULL, 0);
                    if (log != NULL)
                    {
                        fprintf(log, "UT_BSP_Setup: strtoul(parsed)=%lu from ArgV[1]=%p\n",
                                parsed, (void *)ArgV[1]);
                    }
                    UserShift = (uint8)parsed;
                }

                ++ArgV;
            }

            ++ArgV;

            if (log != NULL)
            {
                fprintf(log, "UT_BSP_Setup: advanced ArgV to %p, next Arg (if any) ArgV[0]=%p ('%s'), remaining ArgC=%u\n",
                        (void *)ArgV, (void *)(ArgV[0]), ArgV[0] ? ArgV[0] : "(null)", (unsigned)ArgC);
            }
        }
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        if (log != NULL) fprintf(log, "UT_BSP_Setup: UserShift (%u) invalid -> set to PASS\n", (unsigned)UserShift);
        UserShift = UTASSERT_CASETYPE_PASS;
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;
    if (log != NULL) fprintf(log, "UT_BSP_Setup: final UserShift=%u, CurrVerbosity=0x%X\n",
                              (unsigned)UserShift, (unsigned)BSP_UT_Global.CurrVerbosity);

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");

    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: exit\n\n");
        fclose(log);
    }
}
