void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;
    FILE *log = fopen("/tmp/logs/2955.txt", "a");

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: BSP_UT_Global zeroed\n");
    }

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: OS_BSP_GetArgC returned %u\n", (unsigned)ArgC);
    }

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();
        if (log)
        {
            const char *a0 = (ArgV && ArgV[0]) ? ArgV[0] : "(null)";
            fprintf(log, "UT_BSP_Setup: OS_BSP_GetArgV returned %p, ArgV[0]=%p (\"%s\")\n",
                    (void *)ArgV, (void *)ArgV[0], a0);
        }

        while (ArgC > 0)
        {
            --ArgC;

            if (log)
            {
                const char *cur = (ArgV && ArgV[0]) ? ArgV[0] : "(null)";
                fprintf(log, "UT_BSP_Setup: loop start - remaining ArgC=%u, ArgV=%p, ArgV[0]=%p (\"%s\")\n",
                        (unsigned)ArgC, (void *)ArgV, (void *)ArgV[0], cur);
            }

            if (strcmp(ArgV[0], "-d") == 0)
            {
                UserShift = UTASSERT_CASETYPE_DEBUG;
                if (log)
                {
                    fprintf(log, "UT_BSP_Setup: matched -d => UserShift=%u\n", (unsigned)UserShift);
                }
            }
            if (strcmp(ArgV[0], "-q") == 0)
            {
                UserShift = UTASSERT_CASETYPE_FAILURE;
                if (log)
                {
                    fprintf(log, "UT_BSP_Setup: matched -q => UserShift=%u\n", (unsigned)UserShift);
                }
            }
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                --ArgC;
                /* Log the value being parsed. ArgV[1] is expected to be present because ArgC>0. */
                const char *val = (ArgV[1]) ? ArgV[1] : "(null)";
                if (log)
                {
                    fprintf(log, "UT_BSP_Setup: matched -v, parsing ArgV[1]=%p (\"%s\")\n",
                            (void *)ArgV[1], val);
                }

                {
                    unsigned long parsed = strtoul(ArgV[1], NULL, 0);
                    UserShift = (uint8)parsed;
                    if (log)
                    {
                        fprintf(log, "UT_BSP_Setup: strtoul returned %lu => UserShift=%u\n",
                                parsed, (unsigned)UserShift);
                    }
                }

                ++ArgV; /* advance past the value consumed by -v */
            }

            ++ArgV; /* advance to the next argument */

            if (log)
            {
                fprintf(log, "UT_BSP_Setup: loop end - next ArgV=%p, next ArgV[0]=%p (\"%s\")\n",
                        (void *)ArgV,
                        (void *)(ArgV ? ArgV[0] : NULL),
                        (ArgV && ArgV[0]) ? ArgV[0] : "(null)");
            }
        }
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        if (log)
        {
            fprintf(log, "UT_BSP_Setup: UserShift (%u) was NONE or >= MAX, setting to PASS\n", (unsigned)UserShift);
        }
        UserShift = UTASSERT_CASETYPE_PASS;
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: final UserShift=%u, CurrVerbosity=%u\n",
                (unsigned)UserShift, (unsigned)BSP_UT_Global.CurrVerbosity);
        fflush(log);
        fclose(log);
    }

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");
}
