void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;

    FILE *logf = NULL;
    /* Try to open log file for append. If unavailable, continue without logging. */
    logf = fopen("/tmp/logs/2955.txt", "a");
    if (logf)
    {
        fprintf(logf, "UT_BSP_Setup: entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();

    if (logf)
    {
        fprintf(logf, "UT_BSP_Setup: initial ArgC=%u\n", (unsigned)ArgC);
    }

    if (ArgC > 0)
    {
        uint32 originalArgC = ArgC;
        ArgV = OS_BSP_GetArgV();

        if (logf)
        {
            fprintf(logf, "UT_BSP_Setup: ArgV pointer=%p\n", (void *)ArgV);
            /* Log first few argv entries if present */
            if (ArgV[0] != NULL)
            {
                fprintf(logf, "UT_BSP_Setup: ArgV[0] = '%s'\n", ArgV[0]);
            }
            else
            {
                fprintf(logf, "UT_BSP_Setup: ArgV[0] is NULL\n");
            }
        }

        while (ArgC > 0)
        {
            --ArgC;
            uint32 iter = originalArgC - ArgC - 1u; /* zero-based index of current token */

            if (logf)
            {
                fprintf(logf, "UT_BSP_Setup: loop iter=%u, ArgV(ptr)=%p, ArgV[0]=%s, remaining ArgC=%u\n",
                        (unsigned)iter, (void *)ArgV, (ArgV[0] ? ArgV[0] : "(null)"), (unsigned)ArgC);
            }

            if (strcmp(ArgV[0], "-d") == 0)
            {
                if (logf) fprintf(logf, "UT_BSP_Setup: matched '-d' at iter=%u\n", (unsigned)iter);
                UserShift = UTASSERT_CASETYPE_DEBUG;
            }
            if (strcmp(ArgV[0], "-q") == 0)
            {
                if (logf) fprintf(logf, "UT_BSP_Setup: matched '-q' at iter=%u\n", (unsigned)iter);
                UserShift = UTASSERT_CASETYPE_FAILURE;
            }
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                if (logf) fprintf(logf, "UT_BSP_Setup: matched '-v' at iter=%u, will consume next token\n", (unsigned)iter);
                --ArgC;

                /* Log the token used for verbosity conversion */
                if (logf)
                {
                    if (ArgV[1] != NULL)
                    {
                        fprintf(logf, "UT_BSP_Setup: ArgV[1] for -v = '%s'\n", ArgV[1]);
                    }
                    else
                    {
                        fprintf(logf, "UT_BSP_Setup: ArgV[1] for -v is NULL\n");
                    }
                }

                UserShift = strtoul(ArgV[1], NULL, 0);

                if (logf) fprintf(logf, "UT_BSP_Setup: parsed UserShift=%u from ArgV[1]\n", (unsigned)UserShift);

                ++ArgV; /* advance past the consumed value (pointer increment) */

                if (logf) fprintf(logf, "UT_BSP_Setup: ArgV advanced to %p after consuming value for -v\n", (void *)ArgV);
            }

            ++ArgV; /* advance to next token as in original code */

            if (logf) fprintf(logf, "UT_BSP_Setup: ArgV advanced to %p at loop end\n", (void *)ArgV);
        }
    }
    else
    {
        if (logf)
        {
            fprintf(logf, "UT_BSP_Setup: no arguments (ArgC==0)\n");
        }
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        if (logf) fprintf(logf, "UT_BSP_Setup: UserShift (%u) invalid or none, defaulting to PASS\n", (unsigned)UserShift);
        UserShift = UTASSERT_CASETYPE_PASS;
    }
    else
    {
        if (logf) fprintf(logf, "UT_BSP_Setup: final UserShift=%u\n", (unsigned)UserShift);
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;

    if (logf) fprintf(logf, "UT_BSP_Setup: set CurrVerbosity=%u\n", (unsigned)BSP_UT_Global.CurrVerbosity);

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");

    if (logf)
    {
        fprintf(logf, "UT_BSP_Setup: exiting\n\n");
        fclose(logf);
    }
}
