void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;
    FILE *log = fopen("/tmp/logs/2955.txt", "a");

    /* Ensure log file was opened; if not, continue silently to avoid affecting behavior */
    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();

    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: initial ArgC=%lu\n", (unsigned long)ArgC);
    }

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();

        if (log != NULL)
        {
            fprintf(log, "UT_BSP_Setup: ArgV pointer=%p\n", (void *)ArgV);
        }

        while (ArgC > 0)
        {
            --ArgC;

            /* Log current argument pointer and value (safely) */
            if (log != NULL)
            {
                const char *cur = (ArgV != NULL && ArgV[0] != NULL) ? ArgV[0] : "(null)";
                fprintf(log, "UT_BSP_Setup: loop start - remaining ArgC=%lu, ArgV=%p, ArgV[0]=%s\n",
                        (unsigned long)ArgC + 1UL, (void *)ArgV, cur);
            }

            if (strcmp(ArgV[0], "-d") == 0)
            {
                if (log != NULL)
                {
                    fprintf(log, "UT_BSP_Setup: matched '-d' -> set debug\n");
                }
                UserShift = UTASSERT_CASETYPE_DEBUG;
            }
            if (strcmp(ArgV[0], "-q") == 0)
            {
                if (log != NULL)
                {
                    fprintf(log, "UT_BSP_Setup: matched '-q' -> set failure\n");
                }
                UserShift = UTASSERT_CASETYPE_FAILURE;
            }
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                --ArgC;

                if (log != NULL)
                {
                    const char *val = (ArgV[1] != NULL) ? ArgV[1] : "(null)";
                    fprintf(log, "UT_BSP_Setup: matched '-v' -> parse next arg '%s'\n", val);
                }

                UserShift = strtoul(ArgV[1], NULL, 0);
                ++ArgV; /* advance to skip the value we consumed */
            }

            if (log != NULL)
            {
                fprintf(log, "UT_BSP_Setup: UserShift now=%u\n", (unsigned int)UserShift);
            }

            ++ArgV;
        }
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        if (log != NULL)
        {
            fprintf(log, "UT_BSP_Setup: UserShift (%u) is none or out of range, set to PASS\n", (unsigned int)UserShift);
        }
        UserShift = UTASSERT_CASETYPE_PASS;
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;

    if (log != NULL)
    {
        fprintf(log, "UT_BSP_Setup: final UserShift=%u, CurrVerbosity=%u\n",
                (unsigned int)UserShift, (unsigned int)BSP_UT_Global.CurrVerbosity);
        fflush(log);
        fclose(log);
    }

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");
}
