void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;

    /* Open log for append; best-effort only (don't abort on failure) */
    FILE *log = fopen("/tmp/logs/2955.txt", "a");
    if (log)
    {
        fprintf(log, "Entered UT_BSP_Setup()\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();
    if (log)
    {
        fprintf(log, "Initial ArgC=%u\n", (unsigned)ArgC);
    }

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();
        if (log)
        {
            fprintf(log, "Initial ArgV pointer=%p\n", (void *)ArgV);
        }

        while (ArgC > 0)
        {
            --ArgC;
            if (log)
            {
                const char *cur = (ArgV && ArgV[0]) ? ArgV[0] : "(null)";
                fprintf(log, "Loop: remaining ArgC=%u, ArgV=%p, ArgV[0]=%s\n", (unsigned)ArgC, (void *)ArgV, cur);
            }

            if (strcmp(ArgV[0], "-d") == 0)
            {
                if (log) fprintf(log, "Matched -d: setting UserShift to UTASSERT_CASETYPE_DEBUG\n");
                UserShift = UTASSERT_CASETYPE_DEBUG;
            }
            if (strcmp(ArgV[0], "-q") == 0)
            {
                if (log) fprintf(log, "Matched -q: setting UserShift to UTASSERT_CASETYPE_FAILURE\n");
                UserShift = UTASSERT_CASETYPE_FAILURE;
            }
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                if (log) fprintf(log, "Matched -v and ArgC>0: will parse next token as verbosity\n");
                --ArgC;
                if (log)
                {
                    const char *next = (ArgV[1] != NULL) ? ArgV[1] : "(null)";
                    fprintf(log, "Parsing strtoul on ArgV[1]=%s\n", next);
                }
                UserShift = strtoul(ArgV[1], NULL, 0);
                if (log) fprintf(log, "Parsed UserShift=%u\n", (unsigned)UserShift);
                ++ArgV;
            }

            ++ArgV;
            if (log) fprintf(log, "Advanced ArgV -> %p, next ArgV[0]=%s\n", (void *)ArgV, (ArgV && ArgV[0]) ? ArgV[0] : "(null)");
        }

        if (log) fprintf(log, "Exited parsing loop. Final UserShift=%u\n", (unsigned)UserShift);
    }
    else
    {
        if (log) fprintf(log, "No arguments (ArgC==0)\n");
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        if (log) fprintf(log, "UserShift out of range or NONE: resetting to UTASSERT_CASETYPE_PASS\n");
        UserShift = UTASSERT_CASETYPE_PASS;
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;
    if (log) fprintf(log, "Set BSP_UT_Global.CurrVerbosity = %u\n", (unsigned)BSP_UT_Global.CurrVerbosity);

    if (log) fclose(log);

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");
}
