void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;

    /* Logging probe: open log file to capture control flow and state */
    FILE *log = fopen("/tmp/logs/2955.txt", "a");
    if (log)
    {
        fprintf(log, "UT_BSP_Setup entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();
    if (log) fprintf(log, "Initial ArgC=%lu\n", (unsigned long)ArgC);

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();
        if (log) fprintf(log, "ArgV ptr=%p\n", (void *)ArgV);

        while (ArgC > 0)
        {
            --ArgC;

            /* Log current ArgV[0] pointer and contents (safely) */
            if (log)
            {
                void *arg0_ptr = (void *)(ArgV ? ArgV[0] : NULL);
                fprintf(log, "Loop start: remaining ArgC=%lu, ArgV=%p, ArgV[0]=%p\n",
                        (unsigned long)ArgC, (void *)ArgV, arg0_ptr);
                if (ArgV && ArgV[0])
                {
                    char buf[128];
                    size_t i;
                    for (i = 0; i < sizeof(buf) - 1 && ArgV[0][i]; ++i)
                    {
                        buf[i] = ArgV[0][i];
                    }
                    buf[i] = '\0';
                    fprintf(log, "ArgV[0] string=\"%s\"\n", buf);
                }
                else
                {
                    fprintf(log, "ArgV[0] is NULL or ArgV is NULL\n");
                }
            }

            /* Preserve original comparisons and behavior; add logging of strcmp results */
            {
                int cmp = strcmp(ArgV[0], "-d");
                if (log) fprintf(log, "strcmp(ArgV[0], \"-d\") returned %d\n", cmp);
                if (cmp == 0)
                {
                    UserShift = UTASSERT_CASETYPE_DEBUG;
                    if (log) fprintf(log, "UserShift set to UTASSERT_CASETYPE_DEBUG\n");
                }
            }

            {
                int cmp = strcmp(ArgV[0], "-q");
                if (log) fprintf(log, "strcmp(ArgV[0], \"-q\") returned %d\n", cmp);
                if (cmp == 0)
                {
                    UserShift = UTASSERT_CASETYPE_FAILURE;
                    if (log) fprintf(log, "UserShift set to UTASSERT_CASETYPE_FAILURE\n");
                }
            }

            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                if (log) fprintf(log, "Found -v and ArgC>0 (ArgC=%lu)\n", (unsigned long)ArgC);
                --ArgC;
                if (log) fprintf(log, "Will parse verbosity from ArgV[1]=%p\n", (void *)ArgV[1]);
                UserShift = strtoul(ArgV[1], NULL, 0);
                if (log) fprintf(log, "Parsed UserShift=%lu\n", (unsigned long)UserShift);
                ++ArgV;
            }

            ++ArgV;
            if (log) fprintf(log, "Advanced ArgV to %p\n", (void *)ArgV);
        }
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        UserShift = UTASSERT_CASETYPE_PASS;
        if (log) fprintf(log, "UserShift normalized to UTASSERT_CASETYPE_PASS\n");
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;
    if (log) fprintf(log, "Final CurrVerbosity=%lu\n", (unsigned long)BSP_UT_Global.CurrVerbosity);

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");

    if (log)
    {
        fprintf(log, "UT_BSP_Setup exiting\n\n");
        fclose(log);
    }
}
