static void UT_ClearStubEntry(UT_StubTableEntry_t *StubPtr)
{
    /* Logging to help reproduce potential indirect recursion call chains involving callbacks.
       Logs are appended to /tmp/logs/2520.txt; if that file cannot be opened, fallback to /tmp/2520.txt. */
    FILE *log = fopen("/tmp/logs/2520.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/2520.txt", "a");
    }

    if (log)
    {
        fprintf(log, "UT_ClearStubEntry ENTER: StubPtr=%p\n", (void *)StubPtr);
        /* Log the visible fields used by this function. This mirrors the checks below so the
           exact control path can be reconstructed from the logs. */
        fprintf(log, "  EntryType=%llu, ModeFlags=0x%llx, Data.Buff.BasePtr=%p\n",
                (unsigned long long)StubPtr->EntryType,
                (unsigned long long)StubPtr->ModeFlags,
                (void *)StubPtr->Data.Buff.BasePtr);
        fflush(log);
    }

    /* Be sure to call free() on any malloc'ed buffers before clearing */
    if ((StubPtr->EntryType == UT_ENTRYTYPE_DATA_BUFFER || StubPtr->EntryType == UT_ENTRYTYPE_RETURN_BUFFER) &&
        StubPtr->Data.Buff.BasePtr != NULL && (StubPtr->ModeFlags & UT_MODEFLAG_ALLOC_BUF) != 0)
    {
        if (log)
        {
            fprintf(log, "  Condition true: freeing Data.Buff.BasePtr=%p\n", (void *)StubPtr->Data.Buff.BasePtr);
            fflush(log);
        }
        free(StubPtr->Data.Buff.BasePtr);
        if (log)
        {
            fprintf(log, "  free() completed for %p\n", (void *)StubPtr->Data.Buff.BasePtr);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "  About to memset(StubPtr=%p, size=%zu)\n", (void *)StubPtr, sizeof(*StubPtr));
        fflush(log);
    }

    memset(StubPtr, 0, sizeof(*StubPtr));

    if (log)
    {
        fprintf(log, "UT_ClearStubEntry EXIT: StubPtr=%p now zeroed\n", (void *)StubPtr);
        fclose(log);
    }
}
