static void UT_ClearStubEntry(UT_StubTableEntry_t *StubPtr)
{
    /* Ensure log directory exists (best-effort). This helps tests create the log file. */
    (void)system("mkdir -p /tmp/logs");

    FILE *f = fopen("/tmp/logs/2525.txt", "a");
    if (f != NULL)
    {
        fprintf(f, "UT_ClearStubEntry ENTER: StubPtr=%p\n", (void *)StubPtr);
        /* Log basic control/state info available from the struct */
        fprintf(f, " EntryType=%d ModeFlags=0x%08x\n",
                (int)StubPtr->EntryType,
                (unsigned)StubPtr->ModeFlags);

        fprintf(f, " Buff.BasePtr=%p\n", (void *)StubPtr->Data.Buff.BasePtr);

        /* Read ContentTypeStr as const char * to avoid discarding const at this point. */
        const char *cts = StubPtr->Data.Buff.ContentTypeStr;
        fprintf(f, " Buff.ContentTypeStr=%p", (void *)cts);
        if (cts != NULL)
        {
            /* Print the string value (if it's a valid C string) and a short hex preview. */
            fprintf(f, " \"%s\"\n", cts);
            fprintf(f, " ContentTypeStr preview (hex):");
            for (size_t i = 0; i < 64 && cts[i] != '\0'; ++i)
            {
                fprintf(f, " %02x", (unsigned char)cts[i]);
            }
            fprintf(f, "\n");
        }
        else
        {
            fprintf(f, " (NULL)\n");
        }
    }

    /* Preserve original freeing logic */
    if ((StubPtr->EntryType == UT_ENTRYTYPE_DATA_BUFFER || StubPtr->EntryType == UT_ENTRYTYPE_RETURN_BUFFER) &&
        StubPtr->Data.Buff.BasePtr != NULL && (StubPtr->ModeFlags & UT_MODEFLAG_ALLOC_BUF) != 0)
    {
        if (f != NULL)
        {
            fprintf(f, " Freeing BasePtr=%p\n", (void *)StubPtr->Data.Buff.BasePtr);
        }
        free(StubPtr->Data.Buff.BasePtr);
        if (f != NULL)
        {
            fprintf(f, " Freed BasePtr\n");
        }
    }

    if (f != NULL)
    {
        fprintf(f, " About to memset(StubPtr,0,sizeof(*StubPtr)) size=%zu\n", sizeof(*StubPtr));
    }

    /* Clear the stub entry as before */
    memset(StubPtr, 0, sizeof(*StubPtr));

    if (f != NULL)
    {
        fprintf(f, " UT_ClearStubEntry EXIT: StubPtr=%p (cleared)\n\n", (void *)StubPtr);
        fclose(f);
    }
}
