int32 UT_DefaultStubImpl(const char *FunctionName, UT_EntryKey_t FuncKey, int32 DefaultRc, ...)
{
    int32 Retcode;
    va_list va;

    /* Function-local persistent counter to detect (indirect) recursion depth */
    static int recursion_depth = 0;

    /* Open log file for append; best-effort logging, do not fail on log errors */
    FILE *log = fopen("/tmp/logs/430.txt", "a");
    if (log)
    {
        recursion_depth++;
        fprintf(log, "ENTRY: UT_DefaultStubImpl\n");
        fprintf(log, "  FunctionName: %s\n", FunctionName ? FunctionName : "(null)");
        /* Print the FuncKey and DefaultRc. Cast FuncKey to unsigned long long for a broad range of integral types. */
        fprintf(log, "  FuncKey: %llu\n", (unsigned long long)FuncKey);
        fprintf(log, "  DefaultRc: %d\n", DefaultRc);
        fprintf(log, "  recursion_depth (after increment): %d\n", recursion_depth);
        if (recursion_depth > 1)
        {
            fprintf(log, "  WARNING: recursion detected (depth %d) -- this may indicate indirect recursion / Rule 17.2 violation\n", recursion_depth);
        }
        fflush(log);
    }

    va_start(va, DefaultRc);

    if (log)
    {
        fprintf(log, "  About to call UT_DefaultStubImplWithArgs\n");
        fflush(log);
    }

    /* Preserve original behavior: forward the varargs to the WithArgs variant */
    Retcode = UT_DefaultStubImplWithArgs(FunctionName, FuncKey, DefaultRc, va);

    va_end(va);

    if (log)
    {
        fprintf(log, "  Returned from UT_DefaultStubImplWithArgs, Retcode=%d\n", Retcode);
        /* Decrement recursion counter and log resulting depth */
        recursion_depth--;
        fprintf(log, "  recursion_depth (after decrement): %d\n", recursion_depth);
        fprintf(log, "EXIT: UT_DefaultStubImpl\n\n");
        fclose(log);
    }

    return Retcode;
}
