static void UT_DoSetHookFunction(UT_EntryKey_t FuncKey, UT_EntryType_t EntryType, UT_HookFuncPtr_t Value, void *UserObj,
                                 bool IsVarg)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *logf = fopen("/tmp/logs/35.txt", "a");

    /* Minimal entry log to show function was entered and the parameters observed */
    if (logf != NULL)
    {
        fprintf(logf, "UT_DoSetHookFunction ENTER\n");
        fprintf(logf, "  FuncKey=%llu EntryType=%llu Value.Addr=%p UserObj=%p IsVarg=%d\n",
                (unsigned long long)FuncKey, (unsigned long long)EntryType, (void *)Value.Addr, UserObj, (int)IsVarg);
        fflush(logf);
    }

    /*
     * First find an existing hook entry for the function.
     * In case one is already set we do not duplicate
     */
    StubPtr = UT_GetStubEntry(FuncKey, EntryType);

    if (logf != NULL)
    {
        fprintf(logf, "  After first UT_GetStubEntry: StubPtr=%p\n", (void *)StubPtr);
        fflush(logf);
    }

    if (StubPtr == NULL && Value.Addr != NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "  No existing entry and Value.Addr != NULL => creating force fail entry (search unused)\n");
            fflush(logf);
        }

        /* Creating force fail entry - repeat search and grab any unused slot */
        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);

        if (logf != NULL)
        {
            fprintf(logf, "  After UT_GetStubEntry(UT_ENTRYTYPE_UNUSED): StubPtr=%p\n", (void *)StubPtr);
            fflush(logf);
        }

        if (StubPtr == NULL)
        {
            if (logf != NULL)
            {
                fprintf(logf, "  Cannot set hook function - UT_MAX_FUNC_STUBS too low? About to call UtAssert_Abort\n");
                fflush(logf);
                fclose(logf);
                logf = NULL;
            }
            UtAssert_Abort("Cannot set hook function - UT_MAX_FUNC_STUBS too low?");
        }
    }

    if (Value.Addr == NULL && StubPtr != NULL)
    {
        /* Caller wants to delete the entry */
        if (logf != NULL)
        {
            fprintf(logf, "  Deleting entry at StubPtr=%p (Value.Addr == NULL)\n", (void *)StubPtr);
            fflush(logf);
        }

        UT_ClearStubEntry(StubPtr);

        if (logf != NULL)
        {
            fprintf(logf, "  Entry deleted at StubPtr=%p\n", (void *)StubPtr);
            fflush(logf);
        }
    }
    else if (StubPtr != NULL && Value.Addr != NULL)
    {
        /* Caller wants to set the entry */
        if (logf != NULL)
        {
            /* Log previous values if available (best-effort) */
            fprintf(logf, "  Setting entry at StubPtr=%p\n", (void *)StubPtr);
            fprintf(logf, "    New: FuncKey=%llu EntryType=%llu UserObj=%p Ptr=%p IsVarg=%d\n",
                    (unsigned long long)FuncKey, (unsigned long long)EntryType, UserObj, (void *)Value.Addr, (int)IsVarg);
            fflush(logf);
        }

        StubPtr->FuncKey             = FuncKey;
        StubPtr->EntryType           = EntryType;
        StubPtr->Data.Cb.CallbackArg = UserObj;
        StubPtr->Data.Cb.Ptr         = Value;
        StubPtr->Data.Cb.IsVarg      = IsVarg;

        if (logf != NULL)
        {
            fprintf(logf, "  Entry set at StubPtr=%p\n", (void *)StubPtr);
            fflush(logf);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "UT_DoSetHookFunction EXIT\n\n");
        fclose(logf);
    }
}
