static void UT_DoSetHookFunction(UT_EntryKey_t FuncKey, UT_EntryType_t EntryType, UT_HookFuncPtr_t Value, void *UserObj,
                                 bool IsVarg)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *log = NULL;

    /* Try to open the log file for append. If this fails, proceed without logging. */
    log = fopen("/tmp/logs/35.txt", "a");
    if (log != NULL)
    {
        /* Log entry into the function with the important state values */
        fprintf(log, "ENTER: UT_DoSetHookFunction - FuncKey=%lu EntryType=%lu Value.Addr=%p UserObj=%p IsVarg=%d\n",
                (unsigned long)FuncKey, (unsigned long)EntryType, (void *)Value.Addr, UserObj, IsVarg ? 1 : 0);
        fflush(log);
    }

    /*
     * First find an existing hook entry for the function.
     * In case one is already set we do not duplicate
     */
    StubPtr = UT_GetStubEntry(FuncKey, EntryType);
    if (log != NULL)
    {
        fprintf(log, "After UT_GetStubEntry(FuncKey, EntryType): StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr == NULL && Value.Addr != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "StubPtr == NULL && Value.Addr != NULL: searching for UT_ENTRYTYPE_UNUSED\n");
            fflush(log);
        }

        /* Creating force fail entry - repeat search and grab any unused slot */
        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);
        if (log != NULL)
        {
            fprintf(log, "After UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED): StubPtr=%p\n", (void *)StubPtr);
            fflush(log);
        }

        if (StubPtr == NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "ERROR: Cannot find unused stub slot - about to call UtAssert_Abort\n");
                fflush(log);
                fclose(log);
                log = NULL;
            }

            /* Preserve original behavior (this likely aborts) */
            UtAssert_Abort("Cannot set hook function - UT_MAX_FUNC_STUBS too low?");
            /* In case UtAssert_Abort returns for some build/config, ensure function returns. */
            return;
        }
    }

    if (Value.Addr == NULL && StubPtr != NULL)
    {
        /* Caller wants to delete the entry */
        if (log != NULL)
        {
            fprintf(log, "Action: Delete entry at StubPtr=%p (Value.Addr == NULL)\n", (void *)StubPtr);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        UT_ClearStubEntry(StubPtr);
    }
    else if (StubPtr != NULL && Value.Addr != NULL)
    {
        /* Caller wants to set the entry */
        if (log != NULL)
        {
            fprintf(log, "Action: Set entry at StubPtr=%p\n", (void *)StubPtr);
            fflush(log);
        }

        StubPtr->FuncKey             = FuncKey;
        StubPtr->EntryType           = EntryType;
        StubPtr->Data.Cb.CallbackArg = UserObj;
        StubPtr->Data.Cb.Ptr         = Value;
        StubPtr->Data.Cb.IsVarg      = IsVarg;

        if (log != NULL)
        {
            /* Log the stored values to show that the pointer was copied, not modified */
            fprintf(log, "Stored: StubPtr->FuncKey=%lu StubPtr->EntryType=%lu CallbackArg=%p Ptr.Addr=%p IsVarg=%d\n",
                    (unsigned long)StubPtr->FuncKey,
                    (unsigned long)StubPtr->EntryType,
                    (void *)StubPtr->Data.Cb.CallbackArg,
                    (void *)StubPtr->Data.Cb.Ptr.Addr,
                    StubPtr->Data.Cb.IsVarg ? 1 : 0);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }
    else
    {
        /* No action taken (either both NULL or StubPtr == NULL and Value.Addr == NULL) */
        if (log != NULL)
        {
            fprintf(log, "No action taken: StubPtr=%p Value.Addr=%p\n", (void *)StubPtr, (void *)Value.Addr);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    /* Ensure the log file is closed if still open */
    if (log != NULL)
    {
        fclose(log);
    }
}
