static void UT_DoSetHookFunction(UT_EntryKey_t FuncKey, UT_EntryType_t EntryType, UT_HookFuncPtr_t Value, void *UserObj,
                                 bool IsVarg)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/35.txt", "a");

    if (log != NULL)
    {
        /* Log entry and incoming parameter values to help reproduce violation scenario */
        fprintf(log, "ENTER UT_DoSetHookFunction: FuncKey=%lu EntryType=%lu Value.Addr=%p UserObj=%p IsVarg=%d\n",
                (unsigned long)FuncKey, (unsigned long)EntryType, (void *)Value.Addr, UserObj, (int)IsVarg);
        fflush(log);
    }

    /*
     * First find an existing hook entry for the function.
     * In case one is already set we do not duplicate
     */
    StubPtr = UT_GetStubEntry(FuncKey, EntryType);

    if (log != NULL)
    {
        fprintf(log, "After first lookup: StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr == NULL && Value.Addr != NULL)
    {
        /* Creating force fail entry - repeat search and grab any unused slot */
        if (log != NULL)
        {
            fprintf(log, "No existing entry found and Value.Addr != NULL -> try UT_ENTRYTYPE_UNUSED\n");
            fflush(log);
        }

        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);

        if (log != NULL)
        {
            fprintf(log, "After unused lookup: StubPtr=%p\n", (void *)StubPtr);
            fflush(log);
        }

        if (StubPtr == NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "Cannot set hook function - UT_MAX_FUNC_STUBS too low? About to call UtAssert_Abort\n");
                fclose(log);
            }
            UtAssert_Abort("Cannot set hook function - UT_MAX_FUNC_STUBS too low?");
            /* In case UtAssert_Abort returns, ensure we don't continue */
            return;
        }
    }

    if (Value.Addr == NULL && StubPtr != NULL)
    {
        /* Caller wants to delete the entry */
        if (log != NULL)
        {
            fprintf(log, "Deleting entry: StubPtr=%p\n", (void *)StubPtr);
            fflush(log);
        }

        UT_ClearStubEntry(StubPtr);
    }
    else if (StubPtr != NULL && Value.Addr != NULL)
    {
        /* Caller wants to set the entry */
        if (log != NULL)
        {
            /* Log current callback arg (if any) and the values being set. This helps reproduce the scenario where a non-const UserObj
             * is stored into CallbackArg, showing the pointer values and control path. */
            fprintf(log, "Setting entry: StubPtr=%p FuncKey=%lu EntryType=%lu CallbackArg(before)=%p Value.Addr=%p IsVarg=%d\n",
                    (void *)StubPtr, (unsigned long)FuncKey, (unsigned long)EntryType,
                    (void *)StubPtr->Data.Cb.CallbackArg, (void *)Value.Addr, (int)IsVarg);
            fflush(log);
        }

        StubPtr->FuncKey             = FuncKey;
        StubPtr->EntryType           = EntryType;
        StubPtr->Data.Cb.CallbackArg = UserObj;
        StubPtr->Data.Cb.Ptr         = Value;
        StubPtr->Data.Cb.IsVarg      = IsVarg;

        if (log != NULL)
        {
            /* Log the state after assignment to capture evidence of the assignment that may violate MISRA Rule 8.13 */
            fprintf(log, "Setting entry complete: StubPtr=%p CallbackArg(after)=%p Ptr.Addr=%p IsVarg=%d\n",
                    (void *)StubPtr, (void *)StubPtr->Data.Cb.CallbackArg, (void *)StubPtr->Data.Cb.Ptr.Addr, (int)StubPtr->Data.Cb.IsVarg);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fclose(log);
    }
}
