static void UT_DoSetHookFunction(UT_EntryKey_t FuncKey, UT_EntryType_t EntryType, UT_HookFuncPtr_t Value, void *UserObj,
                                 bool IsVarg)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *logf = fopen("/tmp/logs/35.txt", "a");

    /* Minimal entry log to show function was entered and the initial state */
    if (logf != NULL)
    {
        fprintf(logf, "UT_DoSetHookFunction ENTER: FuncKey=%lu EntryType=%lu Value.Addr=%p UserObj=%p IsVarg=%d\n",
                (unsigned long)FuncKey, (unsigned long)EntryType, (void *)Value.Addr, UserObj, (int)IsVarg);
        fflush(logf);
    }

    /*
     * First find an existing hook entry for the function.
     * In case one is already set we do not duplicate
     */
    StubPtr = UT_GetStubEntry(FuncKey, EntryType);

    if (logf != NULL)
    {
        fprintf(logf, "UT_DoSetHookFunction: After first UT_GetStubEntry -> StubPtr=%p\n", (void *)StubPtr);
        fflush(logf);
    }

    if (StubPtr == NULL && Value.Addr != NULL)
    {
        /* Creating force fail entry - repeat search and grab any unused slot */
        if (logf != NULL)
        {
            fprintf(logf, "UT_DoSetHookFunction: No existing stub and Value.Addr != NULL; searching for unused slot\n");
            fflush(logf);
        }

        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);

        if (logf != NULL)
        {
            fprintf(logf, "UT_DoSetHookFunction: After second UT_GetStubEntry (UNUSED) -> StubPtr=%p\n", (void *)StubPtr);
            fflush(logf);
        }

        if (StubPtr == NULL)
        {
            if (logf != NULL)
            {
                fprintf(logf, "UT_DoSetHookFunction: Cannot set hook function - UT_MAX_FUNC_STUBS too low? About to call UtAssert_Abort()\n");
                fflush(logf);
                fclose(logf);
                logf = NULL;
            }

            UtAssert_Abort("Cannot set hook function - UT_MAX_FUNC_STUBS too low?");
        }
    }

    if (Value.Addr == NULL && StubPtr != NULL)
    {
        /* Caller wants to delete the entry */
        if (logf != NULL)
        {
            fprintf(logf, "UT_DoSetHookFunction: Deleting entry at StubPtr=%p (Value.Addr == NULL)\n", (void *)StubPtr);
            fflush(logf);
            fclose(logf);
            logf = NULL;
        }

        UT_ClearStubEntry(StubPtr);
    }
    else if (StubPtr != NULL && Value.Addr != NULL)
    {
        /* Caller wants to set the entry */
        if (logf != NULL)
        {
            fprintf(logf, "UT_DoSetHookFunction: Setting entry at StubPtr=%p\n", (void *)StubPtr);
            fflush(logf);
        }

        StubPtr->FuncKey             = FuncKey;
        StubPtr->EntryType           = EntryType;
        StubPtr->Data.Cb.CallbackArg = UserObj;
        StubPtr->Data.Cb.Ptr         = Value;
        StubPtr->Data.Cb.IsVarg      = IsVarg;

        if (logf != NULL)
        {
            /* Log the values written into the stub entry so callers can inspect observed state */
            fprintf(logf, "UT_DoSetHookFunction: Written StubPtr->FuncKey=%lu EntryType=%lu CallbackArg=%p Ptr=%p IsVarg=%d\n",
                    (unsigned long)StubPtr->FuncKey, (unsigned long)StubPtr->EntryType,
                    StubPtr->Data.Cb.CallbackArg, (void *)StubPtr->Data.Cb.Ptr.Addr, (int)StubPtr->Data.Cb.IsVarg);
            fflush(logf);
            fclose(logf);
            logf = NULL;
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "UT_DoSetHookFunction: No action taken (StubPtr=%p, Value.Addr=%p)\n", (void *)StubPtr, (void *)Value.Addr);
            fflush(logf);
            fclose(logf);
            logf = NULL;
        }
    }

    /* Ensure file closed if still open */
    if (logf != NULL)
    {
        fclose(logf);
    }
}
