void UT_ExecuteVaHandler(UT_EntryKey_t FuncKey, const char *FunctionName, UT_VaHandlerFunc_t DefaultHandler,
                         va_list VaList)
{
    /* Logging probe: write control-flow and basic state info to /tmp/logs/443.txt */
    FILE *logf = fopen("/tmp/logs/443.txt", "a");
    if (logf != NULL)
    {
        /* Print an entry line showing that the function was entered and some observable state. */
        fprintf(logf, "ENTER UT_ExecuteVaHandler: FunctionName=%s, FuncKey=%lu, DefaultHandler=%s, VaList_addr=%p\n",
                (FunctionName != NULL) ? FunctionName : "(null)",
                (unsigned long)FuncKey,
                (DefaultHandler != NULL) ? "present" : "NULL",
                (void *)&VaList);
        fflush(logf);
    }

    /* Preserve original behavior: check if the test case registered a hook, and set the default if not */
    void *stubEntry = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_FINAL_HANDLER);

    if (logf != NULL)
    {
        fprintf(logf, "UT_GetStubEntry returned %p (NULL means no final handler)\n", stubEntry);
        fflush(logf);
    }

    if (stubEntry == NULL && DefaultHandler != NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "Condition met: stubEntry == NULL && DefaultHandler != NULL -> calling UT_SetVaHandlerFunction\n");
            fflush(logf);
        }

        UT_SetVaHandlerFunction(FuncKey, DefaultHandler, NULL);

        if (logf != NULL)
        {
            fprintf(logf, "UT_SetVaHandlerFunction returned\n");
            fflush(logf);
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "No call to UT_SetVaHandlerFunction. stubEntry %s, DefaultHandler %s\n",
                    (stubEntry == NULL) ? "NULL" : "non-NULL",
                    (DefaultHandler == NULL) ? "NULL" : "present");
            fflush(logf);
        }
    }

    /* Call the default implementation with the provided va_list (preserve original behavior) */
    if (logf != NULL)
    {
        fprintf(logf, "Calling UT_DefaultStubImplWithArgs: FunctionName=%s, FuncKey=%lu, argCount=0, VaList_addr=%p\n",
                (FunctionName != NULL) ? FunctionName : "(null)", (unsigned long)FuncKey, (void *)&VaList);
        fflush(logf);
    }

    UT_DefaultStubImplWithArgs(FunctionName, FuncKey, 0, VaList);

    if (logf != NULL)
    {
        fprintf(logf, "UT_DefaultStubImplWithArgs returned. EXIT UT_ExecuteVaHandler\n\n");
        fclose(logf);
    }
}
