void UT_ExecuteVaHandler(UT_EntryKey_t FuncKey, const char *FunctionName, UT_VaHandlerFunc_t DefaultHandler,
                         va_list VaList)
{
    /* Log probe: record entry and parameters to a file for post-mortem analysis */
    FILE *log = fopen("/tmp/logs/443.txt", "a");
    if (log == NULL)
    {
        /* Fallback if directory /tmp/logs does not exist */
        log = fopen("/tmp/443.txt", "a");
    }

    if (log != NULL)
    {
        /* Print basic call-site information and pointers/values useful to reproduce state */
        fprintf(log, "UT_ExecuteVaHandler: entered\n");
        fprintf(log, "  FuncKey: %llu\n", (unsigned long long)FuncKey);
        fprintf(log, "  FunctionName ptr: %p\n", (const void *)FunctionName);
        if (FunctionName != NULL)
        {
            /* print the function name string if available (avoid crash on NULL) */
            fprintf(log, "  FunctionName str: '%s'\n", FunctionName);
        }
        fprintf(log, "  DefaultHandler ptr: %p\n", (const void *)DefaultHandler);
        fprintf(log, "  VaList addr: %p\n", (void *)&VaList);
        fflush(log);
    }

    /* Preserve original logic but avoid calling UT_GetStubEntry twice; capture the result for logging */
    void *stubEntry = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_FINAL_HANDLER);

    if (log != NULL)
    {
        fprintf(log, "  UT_GetStubEntry returned: %p\n", stubEntry);
        fflush(log);
    }

    if (stubEntry == NULL && DefaultHandler != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "  No final handler registered -> calling UT_SetVaHandlerFunction with DefaultHandler %p\n",
                    (const void *)DefaultHandler);
            fflush(log);
        }

        /* Call remains identical to original behavior */
        UT_SetVaHandlerFunction(FuncKey, DefaultHandler, NULL);
    }
    else
    {
        if (log != NULL)
        {
            if (stubEntry != NULL)
            {
                fprintf(log, "  Final handler already registered (no action taken)\n");
            }
            else
            {
                fprintf(log, "  DefaultHandler is NULL; not setting handler\n");
            }
            fflush(log);
        }
    }

    /* Execute the default stub implementation with the provided args (exactly as original) */
    UT_DefaultStubImplWithArgs(FunctionName, FuncKey, 0, VaList);

    if (log != NULL)
    {
        fprintf(log, "  UT_DefaultStubImplWithArgs returned; exiting UT_ExecuteVaHandler\n");
        fclose(log);
    }
}
