void UT_ExecuteVaHandler(UT_EntryKey_t FuncKey, const char *FunctionName, UT_VaHandlerFunc_t DefaultHandler,
                         va_list VaList)
{
    /* Logging probe: open log file for appending state and control-flow info */
    FILE *log = fopen("/tmp/logs/445.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: UT_ExecuteVaHandler - FunctionName=%s, DefaultHandler=%s\n",
                (FunctionName != NULL) ? FunctionName : "(null)",
                (DefaultHandler != NULL) ? "present" : "NULL");
        fflush(log);
    }

    /* Check if the test case registered a hook, and use the default if not */
    void *stub_entry = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_FINAL_HANDLER);

    if (log != NULL)
    {
        fprintf(log, "UT_GetStubEntry returned %s\n", (stub_entry != NULL) ? "non-NULL" : "NULL");
        fflush(log);
    }

    if (stub_entry == NULL && DefaultHandler != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "Registering DefaultHandler for FunctionName=%s\n",
                    (FunctionName != NULL) ? FunctionName : "(null)");
            fflush(log);
        }

        UT_SetVaHandlerFunction(FuncKey, DefaultHandler, NULL);

        if (log != NULL)
        {
            fprintf(log, "UT_SetVaHandlerFunction completed for FunctionName=%s\n",
                    (FunctionName != NULL) ? FunctionName : "(null)");
            fflush(log);
        }
    }

    /* Preserve original behavior: invoke default stub implementation with args */
    UT_DefaultStubImplWithArgs(FunctionName, FuncKey, 0, VaList);

    if (log != NULL)
    {
        fprintf(log, "EXIT: UT_ExecuteVaHandler - FunctionName=%s\n",
                (FunctionName != NULL) ? FunctionName : "(null)");
        fclose(log);
    }
}
