void UT_GetDataBuffer(UT_EntryKey_t FuncKey, void **DataBuffer, size_t *MaxSize, size_t *Position)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ResultDataBuffer;
    size_t               ResultMaxSize;
    size_t               ResultPosition;

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (StubPtr == NULL)
    {
        ResultDataBuffer = NULL;
        ResultMaxSize    = 0;
        ResultPosition   = 0;
    }
    else
    {
        ResultDataBuffer = StubPtr->Data.Buff.BasePtr;
        ResultMaxSize    = StubPtr->Data.Buff.TotalSize;
        ResultPosition   = StubPtr->Data.Buff.Position;
    }

    /* Logging: append a concise record of the control path and key state values to /tmp/logs/753.txt */
    {
        FILE *log = fopen("/tmp/logs/753.txt", "a");
        if (log != NULL)
        {
            /* Log entry with pointers and sizes to help reproduce any downstream buffer-copy issues. */
            fprintf(log,
                    "UT_GetDataBuffer entered: StubPtr=%p, ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu, DataBuffer_out_ptr=%p, MaxSize_out_ptr=%p, Position_out_ptr=%p\n",
                    (void *)StubPtr,
                    ResultDataBuffer,
                    ResultMaxSize,
                    ResultPosition,
                    (void *)DataBuffer,
                    (void *)MaxSize,
                    (void *)Position);

            if (DataBuffer != NULL)
            {
                fprintf(log, " -> *DataBuffer will be set to %p\n", ResultDataBuffer);
            }
            if (MaxSize != NULL)
            {
                fprintf(log, " -> *MaxSize will be set to %zu\n", ResultMaxSize);
            }
            if (Position != NULL)
            {
                fprintf(log, " -> *Position will be set to %zu\n", ResultPosition);
            }

            fclose(log);
        }
    }

    if (DataBuffer != NULL)
    {
        *DataBuffer = ResultDataBuffer;
    }
    if (MaxSize != NULL)
    {
        *MaxSize = ResultMaxSize;
    }
    if (Position != NULL)
    {
        *Position = ResultPosition;
    }
}
