void UT_GetDataBuffer(UT_EntryKey_t FuncKey, void **DataBuffer, size_t *MaxSize, size_t *Position)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ResultDataBuffer;
    size_t               ResultMaxSize;
    size_t               ResultPosition;

    /* Open log file (append). If opening fails, fall back to stderr. */
    FILE *logf = fopen("/tmp/logs/755.txt", "a");
    if (logf == NULL)
    {
        /* If file cannot be opened, write minimal entry to stderr so the test harness still sees a log. */
        (void)fprintf(stderr, "UT_GetDataBuffer: cannot open /tmp/logs/755.txt, logging to stderr\n");
        (void)fprintf(stderr, "UT_GetDataBuffer: entry: DataBuffer=%p, MaxSize=%p, Position=%p\n",
                      (void *)DataBuffer, (void *)MaxSize, (void *)Position);
    }
    else
    {
        (void)fprintf(logf, "UT_GetDataBuffer: entry: DataBuffer=%p, MaxSize=%p, Position=%p\n",
                      (void *)DataBuffer, (void *)MaxSize, (void *)Position);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (logf != NULL)
    {
        (void)fprintf(logf, "UT_GetDataBuffer: UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
    }
    else
    {
        (void)fprintf(stderr, "UT_GetDataBuffer: UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
    }

    if (StubPtr == NULL)
    {
        ResultDataBuffer = NULL;
        ResultMaxSize    = 0;
        ResultPosition   = 0;

        if (logf != NULL)
        {
            (void)fprintf(logf, "UT_GetDataBuffer: no stub entry -> ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu\n",
                          ResultDataBuffer, ResultMaxSize, ResultPosition);
        }
        else
        {
            (void)fprintf(stderr, "UT_GetDataBuffer: no stub entry -> ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu\n",
                          ResultDataBuffer, ResultMaxSize, ResultPosition);
        }
    }
    else
    {
        /* Record the values read from the stub before assigning to out parameters */
        ResultDataBuffer = StubPtr->Data.Buff.BasePtr;
        ResultMaxSize    = StubPtr->Data.Buff.TotalSize;
        ResultPosition   = StubPtr->Data.Buff.Position;

        if (logf != NULL)
        {
            (void)fprintf(logf, "UT_GetDataBuffer: read from stub -> BasePtr=%p, TotalSize=%zu, Position=%zu\n",
                          (void *)StubPtr->Data.Buff.BasePtr, StubPtr->Data.Buff.TotalSize, StubPtr->Data.Buff.Position);
        }
        else
        {
            (void)fprintf(stderr, "UT_GetDataBuffer: read from stub -> BasePtr=%p, TotalSize=%zu, Position=%zu\n",
                          (void *)StubPtr->Data.Buff.BasePtr, StubPtr->Data.Buff.TotalSize, StubPtr->Data.Buff.Position);
        }
    }

    /* Assign results to output parameters, preserving original behavior */
    if (DataBuffer != NULL)
    {
        *DataBuffer = ResultDataBuffer;
        if (logf != NULL)
        {
            (void)fprintf(logf, "UT_GetDataBuffer: wrote DataBuffer=%p\n", ResultDataBuffer);
        }
        else
        {
            (void)fprintf(stderr, "UT_GetDataBuffer: wrote DataBuffer=%p\n", ResultDataBuffer);
        }
    }
    if (MaxSize != NULL)
    {
        *MaxSize = ResultMaxSize;
        if (logf != NULL)
        {
            (void)fprintf(logf, "UT_GetDataBuffer: wrote MaxSize=%zu\n", ResultMaxSize);
        }
        else
        {
            (void)fprintf(stderr, "UT_GetDataBuffer: wrote MaxSize=%zu\n", ResultMaxSize);
        }
    }
    if (Position != NULL)
    {
        *Position = ResultPosition;
        if (logf != NULL)
        {
            (void)fprintf(logf, "UT_GetDataBuffer: wrote Position=%zu\n", ResultPosition);
        }
        else
        {
            (void)fprintf(stderr, "UT_GetDataBuffer: wrote Position=%zu\n", ResultPosition);
        }
    }

    if (logf != NULL)
    {
        (void)fprintf(logf, "UT_GetDataBuffer: exit\n");
        (void)fclose(logf);
    }
}
