void UT_GetDataBuffer(UT_EntryKey_t FuncKey, void **DataBuffer, size_t *MaxSize, size_t *Position)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ResultDataBuffer;
    size_t               ResultMaxSize;
    size_t               ResultPosition;

    /* Open log file for appending; best-effort logging only. */
    {
        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer ENTER: FuncKey=%p, DataBuffer=%p, MaxSize=%p, Position=%p\n",
                    (void *)FuncKey, (void *)DataBuffer, (void *)MaxSize, (void *)Position);
            fclose(log);
        }
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (StubPtr == NULL)
    {
        ResultDataBuffer = NULL;
        ResultMaxSize    = 0;
        ResultPosition   = 0;

        /* Log the NULL-stub branch and resulting values */
        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer: StubPtr == NULL -> ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu\n",
                    (void *)ResultDataBuffer, ResultMaxSize, ResultPosition);
            fclose(log);
        }
    }
    else
    {
        /* Capture values from stub (this is the assignment that may discard const qualification if member is const) */
        ResultDataBuffer = StubPtr->Data.Buff.BasePtr;
        ResultMaxSize    = StubPtr->Data.Buff.TotalSize;
        ResultPosition   = StubPtr->Data.Buff.Position;

        /* Log the non-NULL branch and the stub-provided values to help reproduce/discover const discard issues */
        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer: StubPtr=%p, BasePtr=%p, TotalSize=%zu, Position(in stub)=%zu\n",
                    (void *)StubPtr,
                    (void *)StubPtr->Data.Buff.BasePtr,
                    StubPtr->Data.Buff.TotalSize,
                    StubPtr->Data.Buff.Position);

            fprintf(log, "UT_GetDataBuffer: After assign -> ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu\n",
                    (void *)ResultDataBuffer, ResultMaxSize, ResultPosition);

            /* Also log the target output pointers that may receive the values */
            fprintf(log, "UT_GetDataBuffer: Output pointers -> DataBuffer=%p, MaxSize=%p, Position=%p\n",
                    (void *)DataBuffer, (void *)MaxSize, (void *)Position);

            fclose(log);
        }
    }

    if (DataBuffer != NULL)
    {
        /* Log the impending write to the caller-provided DataBuffer pointer */
        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer: Writing *DataBuffer = %p -> (this may discard const qualification if BasePtr was const)\n",
                    (void *)ResultDataBuffer);
            fclose(log);
        }

        *DataBuffer = ResultDataBuffer;
    }
    if (MaxSize != NULL)
    {
        *MaxSize = ResultMaxSize;

        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer: Writing *MaxSize = %zu\n", ResultMaxSize);
            fclose(log);
        }
    }
    if (Position != NULL)
    {
        *Position = ResultPosition;

        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer: Writing *Position = %zu\n", ResultPosition);
            fclose(log);
        }
    }

    /* Final log to indicate exit */
    {
        FILE *log = fopen("/tmp/logs/755.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UT_GetDataBuffer EXIT: ResultDataBuffer=%p, ResultMaxSize=%zu, ResultPosition=%zu\n\n",
                    (void *)ResultDataBuffer, ResultMaxSize, ResultPosition);
            fclose(log);
        }
    }
}
