void UT_GetDataBuffer(UT_EntryKey_t FuncKey, void **DataBuffer, size_t *MaxSize, size_t *Position)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ResultDataBuffer;
    size_t               ResultMaxSize;
    size_t               ResultPosition;
    FILE *log = fopen("/tmp/logs/755.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "UT_GetDataBuffer: entered\n");
        fprintf(log, "  FuncKey (raw): %p\n", (void *)&FuncKey);
        fprintf(log, "  DataBuffer ptr: %p, MaxSize ptr: %p, Position ptr: %p\n",
                (void *)DataBuffer, (void *)MaxSize, (void *)Position);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (StubPtr == NULL)
    {
        ResultDataBuffer = NULL;
        ResultMaxSize    = 0;
        ResultPosition   = 0;
        if (log != NULL)
        {
            fprintf(log, "  StubPtr = NULL -> ResultDataBuffer = %p, ResultMaxSize = %zu, ResultPosition = %zu\n",
                    (void *)ResultDataBuffer, ResultMaxSize, ResultPosition);
        }
    }
    else
    {
        ResultDataBuffer = StubPtr->Data.Buff.BasePtr;
        ResultMaxSize    = StubPtr->Data.Buff.TotalSize;
        ResultPosition   = StubPtr->Data.Buff.Position;
        if (log != NULL)
        {
            fprintf(log, "  StubPtr != NULL -> BasePtr = %p, TotalSize = %zu, Position = %zu\n",
                    (void *)ResultDataBuffer, ResultMaxSize, ResultPosition);
        }
    }

    if (DataBuffer != NULL)
    {
        /* Log the assignment without dereferencing the incoming pointer (avoid UB). */
        if (log != NULL)
        {
            fprintf(log, "  Assigning *DataBuffer = %p\n", (void *)ResultDataBuffer);
        }
        *DataBuffer = ResultDataBuffer;
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  DataBuffer is NULL; no assignment\n");
        }
    }

    if (MaxSize != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "  Assigning *MaxSize = %zu\n", ResultMaxSize);
        }
        *MaxSize = ResultMaxSize;
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  MaxSize is NULL; no assignment\n");
        }
    }

    if (Position != NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "  Assigning *Position = %zu\n", ResultPosition);
        }
        *Position = ResultPosition;
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  Position is NULL; no assignment\n");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_GetDataBuffer: exit\n\n");
        fclose(log);
    }
}
