static UT_StubTableEntry_t *UT_GetStubEntry(UT_EntryKey_t FuncKey, UT_EntryType_t TestMode)
{
    UT_StubTableEntry_t *StubPtr   = NULL;
    uint32               Idx       = FuncKey % (UT_MAX_FUNC_STUBS - 1); /* hash the key to determine the start point */
    uint32               SearchLen = 0;
    uint32               SearchLimit;
    UT_EntryKey_t        SearchKey;
    FILE *logf = fopen("/tmp/logs/605.txt", "a");

    if (logf)
    {
        fprintf(logf, "UT_GetStubEntry ENTER: FuncKey=%llu, TestMode=%u, StartIdx=%u\n",
                (unsigned long long)FuncKey, (unsigned)TestMode, (unsigned)Idx);
    }

    /* If searching for an unused entry, look through the entire table.
     * Otherwise bound the search */
    if (TestMode == UT_ENTRYTYPE_UNUSED)
    {
        SearchLimit = UT_MAX_FUNC_STUBS;
        SearchKey   = 0;
    }
    else
    {
        SearchLimit = UT_MaxStubSearchLen;
        SearchKey   = FuncKey;
    }

    if (logf)
    {
        fprintf(logf, "SearchLimit=%u, SearchKey=%llu\n",
                (unsigned)SearchLimit, (unsigned long long)SearchKey);
    }

    while (1)
    {
        if (SearchLen >= SearchLimit)
        {
            StubPtr = NULL;
            if (logf)
            {
                fprintf(logf, "Search exhausted: SearchLen=%u >= SearchLimit=%u -> no entry found\n",
                        (unsigned)SearchLen, (unsigned)SearchLimit);
                fflush(logf);
                fclose(logf);
            }
            break;
        }
        ++SearchLen;
        StubPtr = &UT_StubTable[Idx];

        if (logf)
        {
            /* Log the basic observable fields that this function uses */
            fprintf(logf, "Iter %u: Idx=%u, SearchLen=%u, StubPtr=%p, StubPtr->EntryType=%u, StubPtr->FuncKey=%llu\n",
                    (unsigned)SearchLen, (unsigned)Idx, (unsigned)SearchLen,
                    (void *)StubPtr, (unsigned)StubPtr->EntryType, (unsigned long long)StubPtr->FuncKey);

            /* Attempt to log Data.Buff.ContentTypeStr if present/initialized. This helps reproduce the const-discard scenario
             * by recording the pointer value and the pointed contents (if any). */
            /* NOTE: Accessing this member is safe only if the structure actually contains it. The test environment
             * where the violation was reported does include this member; when present this log will give evidence
             * of assignments of const char* into non-const char* fields (via pointer values and content). */
            /* We protect the string access with a NULL check and limit the printed length. */
            #if 1
            /* The following block relies on the actual UT_StubTableEntry_t layout in the build environment.
             * If the member exists, this will print its pointer and up to 160 bytes of content. */
            if ((void *)StubPtr != NULL)
            {
                /* Use a volatile pointer on the member to avoid certain compiler optimizations that might remove
                 * the read; this makes the logging more likely to observe the actual stored pointer value. */
                /* The code below compiles only if the member exists in the project's definition. */
                if (StubPtr->Data.Buff.ContentTypeStr != (const char *)0)
                {
                    const char *cptr = StubPtr->Data.Buff.ContentTypeStr;
                    char buf[161];
                    size_t i;

                    /* copy safely up to 160 chars */
                    for (i = 0U; i < 160U; ++i)
                    {
                        char ch = cptr[i];
                        buf[i] = ch;
                        if (ch == '\0') break;
                    }
                    buf[(i < 160U) ? i : 160U] = '\0';

                    fprintf(logf, "  ContentTypeStr ptr=%p, starts=\"%s\"\n", (void *)cptr, buf);
                }
                else
                {
                    fprintf(logf, "  ContentTypeStr ptr=NULL\n");
                }
            }
            #endif
            fflush(logf);
        }

        if (StubPtr->EntryType == TestMode && StubPtr->FuncKey == SearchKey)
        {
            if (logf)
            {
                fprintf(logf, "Matching entry found at Idx=%u after SearchLen=%u\n", (unsigned)Idx, (unsigned)SearchLen);
                fflush(logf);
                fclose(logf);
            }
            break;
        }
        ++Idx;
        if (Idx >= UT_MAX_FUNC_STUBS)
        {
            Idx = 0;
        }
    }

    /*
     * Keep track of the longest search length since the last reset.
     * This serves as the upper bound for future searches.
     */
    if (SearchLen > UT_MaxStubSearchLen)
    {
        UT_MaxStubSearchLen = SearchLen;
        /* Log the new maximum length */
        FILE *logf2 = fopen("/tmp/logs/605.txt", "a");
        if (logf2)
        {
            fprintf(logf2, "UT_MaxStubSearchLen updated to %u\n", (unsigned)UT_MaxStubSearchLen);
            fflush(logf2);
            fclose(logf2);
        }
    }

    return StubPtr;
}
