const void *UT_Hook_GetArgPtr(const UT_StubContext_t *ContextPtr, const char *Name, size_t ExpectedTypeSize)
{
    uint32                      i;
    const void *                Result;
    const UT_StubArgMetaData_t *MetaPtr;

    static const union
    {
        unsigned long AsInt;
        void *        AsPtr;
        double        AsFloat;
    } ARG_DEFAULT_ZERO_VALUE = {0};

    /* Open log file for append. Tests expect logs at this path. */
    FILE *log = fopen("/tmp/logs/2765.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "UT_Hook_GetArgPtr ENTER: ContextPtr=%p Name=%s ExpectedTypeSize=%zu\n",
                (void *)ContextPtr, (Name != NULL ? Name : "(null)"), ExpectedTypeSize);
        fflush(log);
    }

    Result = NULL;
    for (i = 0; i < ContextPtr->ArgCount; ++i)
    {
        MetaPtr = &ContextPtr->Meta[i];
        if (log != NULL)
        {
            fprintf(log, "  loop i=%u Meta=%p Meta->Name=%s Meta->Size=%zu Meta->Type=%d\n",
                    (unsigned)i,
                    (void *)MetaPtr,
                    (MetaPtr->Name != NULL ? MetaPtr->Name : "(null)"),
                    (size_t)MetaPtr->Size,
                    (int)MetaPtr->Type);
            fflush(log);
        }

        if (MetaPtr->Name != NULL)
        {
            if (strcmp(MetaPtr->Name, Name) == 0 && (MetaPtr->Size == 0 || MetaPtr->Size == ExpectedTypeSize))
            {
                if (MetaPtr->Type == UT_STUBCONTEXT_ARG_TYPE_DIRECT)
                {
                    /*
                     * Note: This reproduces the original behavior where the address of
                     * the array element is returned (a pointer to the element), which is
                     * the scenario flagged for MISRA Rule 8.13. Preserve that behavior
                     * so callers/tests behave identically.
                     */
                    Result = &ContextPtr->ArgPtr[i];

                    if (log != NULL)
                    {
                        fprintf(log, "    match DIRECT at i=%u -> &ContextPtr->ArgPtr[i]=%p (element value=%p) assigned to Result=%p\n",
                                (unsigned)i,
                                (void *)&ContextPtr->ArgPtr[i],
                                (void *)ContextPtr->ArgPtr[i],
                                (void *)Result);
                        fflush(log);
                    }
                }
                else if (MetaPtr->Type == UT_STUBCONTEXT_ARG_TYPE_INDIRECT)
                {
                    Result = ContextPtr->ArgPtr[i];

                    if (log != NULL)
                    {
                        fprintf(log, "    match INDIRECT at i=%u -> ContextPtr->ArgPtr[i]=%p assigned to Result=%p\n",
                                (unsigned)i,
                                (void *)ContextPtr->ArgPtr[i],
                                (void *)Result);
                        fflush(log);
                    }
                }
                break;
            }
        }
    }

    if (Result == NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "  no suitable result found for Name=%s ExpectedTypeSize=%zu -> invoking UtAssert_Failed\n",
                    (Name != NULL ? Name : "(null)"), ExpectedTypeSize);
            fflush(log);
        }

        UtAssert_Failed("Requested parameter %s of size %lu which was not provided by the stub", Name,
                        (unsigned long)ExpectedTypeSize);

        if (ExpectedTypeSize <= sizeof(ARG_DEFAULT_ZERO_VALUE))
        {
            Result = &ARG_DEFAULT_ZERO_VALUE;

            if (log != NULL)
            {
                fprintf(log, "  using ARG_DEFAULT_ZERO_VALUE at %p as Result=%p\n",
                        (void *)&ARG_DEFAULT_ZERO_VALUE, (void *)Result);
                fflush(log);
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "  ExpectedTypeSize (%zu) too large for default -> invoking UtAssert_Abort\n", ExpectedTypeSize);
                fflush(log);
            }

            UtAssert_Abort("No value for parameter");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_Hook_GetArgPtr EXIT: Result=%p\n\n", (void *)Result);
        fclose(log);
    }

    return Result;
}
