void UT_SetDataBuffer(UT_EntryKey_t FuncKey, void *DataBuffer, size_t BufferSize, bool AllocateCopy)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/2573.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "UT_SetDataBuffer ENTER: FuncKey=%u, DataBuffer=%p, BufferSize=%zu, AllocateCopy=%d\n",
                (unsigned)FuncKey, DataBuffer, BufferSize, (int)AllocateCopy);
        fflush(log);
    }

    if (DataBuffer != NULL && BufferSize > 0)
    {
        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);

        if (log != NULL)
        {
            fprintf(log, "UT_SetDataBuffer: UT_GetStubEntry returned %p\n", (void *)StubPtr);
            fflush(log);
        }

        if (StubPtr == NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "UT_SetDataBuffer ERROR: Cannot set data buffer - UT_MAX_FUNC_STUBS too low?\n");
                fclose(log);
            }
            UtAssert_Abort("Cannot set data buffer - UT_MAX_FUNC_STUBS too low?");
        }
        else
        {
            StubPtr->FuncKey   = FuncKey;
            StubPtr->EntryType = UT_ENTRYTYPE_DATA_BUFFER;

            if (log != NULL)
            {
                fprintf(log, "UT_SetDataBuffer: Assigned FuncKey and EntryType. AllocateCopy=%d\n", (int)AllocateCopy);
                fflush(log);
            }

            if (AllocateCopy)
            {
                if (log != NULL)
                {
                    fprintf(log, "UT_SetDataBuffer: Allocating %zu bytes for copy.\n", BufferSize);
                    fflush(log);
                }

                StubPtr->Data.Buff.BasePtr = malloc(BufferSize);

                if (log != NULL)
                {
                    fprintf(log, "UT_SetDataBuffer: malloc returned %p\n", StubPtr->Data.Buff.BasePtr);
                    fflush(log);
                }

                if (StubPtr->Data.Buff.BasePtr == NULL)
                {
                    if (log != NULL)
                    {
                        fprintf(log, "UT_SetDataBuffer ERROR: Cannot allocate data buffer - malloc() failed!\n");
                        fclose(log);
                    }
                    UtAssert_Abort("Cannot allocate data buffer - malloc() failed!");
                }
                else
                {
                    /* Log about to perform memcpy which may read from DataBuffer */
                    if (log != NULL)
                    {
                        fprintf(log, "UT_SetDataBuffer: About to memcpy(dest=%p, src=%p, n=%zu)\n",
                                StubPtr->Data.Buff.BasePtr, DataBuffer, BufferSize);
                        fflush(log);
                    }

                    memcpy(StubPtr->Data.Buff.BasePtr, DataBuffer, BufferSize);

                    if (log != NULL)
                    {
                        fprintf(log, "UT_SetDataBuffer: memcpy completed\n");
                        fflush(log);
                    }

                    StubPtr->ModeFlags |= UT_MODEFLAG_ALLOC_BUF;

                    if (log != NULL)
                    {
                        fprintf(log, "UT_SetDataBuffer: ModeFlags after setting ALLOC_BUF = 0x%llx\n",
                                (unsigned long long)StubPtr->ModeFlags);
                        fflush(log);
                    }
                }
            }
            else
            {
                /* Use buffer directly */
                StubPtr->Data.Buff.BasePtr = DataBuffer;

                if (log != NULL)
                {
                    fprintf(log, "UT_SetDataBuffer: Using external buffer directly: BasePtr=%p\n", DataBuffer);
                    fflush(log);
                }
            }

            StubPtr->Data.Buff.TotalSize = BufferSize;
            StubPtr->Data.Buff.Position  = 0;

            if (log != NULL)
            {
                fprintf(log, "UT_SetDataBuffer: Final state: BasePtr=%p, TotalSize=%zu, Position=%zu\n",
                        StubPtr->Data.Buff.BasePtr, StubPtr->Data.Buff.TotalSize, (size_t)StubPtr->Data.Buff.Position);
                fclose(log);
            }
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "UT_SetDataBuffer: Entry conditions not met (DataBuffer=%p, BufferSize=%zu) - no action taken\n",
                    DataBuffer, BufferSize);
            fclose(log);
        }
    }
}
