void UT_SetDeferredRetcode(UT_EntryKey_t FuncKey, int32 Count, UT_IntReturn_t Retcode)
{
    /* Minimal logging to help reproduce control flow/state for a potential buffer-copy violation. */
    FILE *log_fp = fopen("/tmp/logs/1833.txt", "a");

    if (log_fp != NULL)
    {
        /* Log entry and parameters */
        fprintf(log_fp, "ENTER: UT_SetDeferredRetcode()\n");
        fprintf(log_fp, "  FuncKey: %lu\n", (unsigned long)FuncKey);
        fprintf(log_fp, "  Count  : %ld\n", (long)Count);
        fprintf(log_fp, "  Retcode: %ld\n", (long)Retcode);
        fflush(log_fp);
    }

    /* Preserve original logic: the count should always be a positive integer */
    if (Count <= 0)
    {
        if (log_fp != NULL)
        {
            fprintf(log_fp, "ERROR: Invalid Count (%ld) passed to UT_SetDeferredRetcode() - invoking UtAssert_Failed()\n", (long)Count);
            fclose(log_fp);
        }

        /* Original behavior preserved */
        UtAssert_Failed("BUG: Invalid Count (%ld) passed to UT_SetDeferredRetcode()", (long)Count);
    }
    else
    {
        if (log_fp != NULL)
        {
            fprintf(log_fp, "ACTION: Calling UT_ConfigureGenericStubReturnValue() with the provided Retcode buffer and Count=%ld\n", (long)Count);
            fflush(log_fp);
        }

        /* Original behavior preserved */
        UT_ConfigureGenericStubReturnValue(FuncKey, &Retcode, sizeof(Retcode), UT_ValueGenre_INTEGER, Count, NULL);

        if (log_fp != NULL)
        {
            fprintf(log_fp, "RETURN: UT_ConfigureGenericStubReturnValue() completed\n");
            fclose(log_fp);
        }
    }
}
