size_t UT_Stub_CopyFromLocal(UT_EntryKey_t FuncKey, const void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *log = NULL;

    /* Attempt to open log file in append mode; if this fails, continue silently */
    log = fopen("/tmp/logs/1233.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: UT_Stub_CopyFromLocal\n");
        fprintf(log, "  LocalBuffer=%p, MaxSize=%llu\n", (const void *)LocalBuffer, (unsigned long long)MaxSize);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (log != NULL)
    {
        fprintf(log, "  UT_GetStubEntry -> %p\n", (void *)StubPtr);
    }

    if (StubPtr != NULL)
    {
        size_t pos = StubPtr->Data.Buff.Position;
        size_t total = StubPtr->Data.Buff.TotalSize;
        void *base = (void *)StubPtr->Data.Buff.BasePtr;

        if (log != NULL)
        {
            fprintf(log, "  Before copy: BasePtr=%p, Position=%llu, TotalSize=%llu\n",
                    base,
                    (unsigned long long)pos,
                    (unsigned long long)total);
        }

        /* Original logic preserved: compute remaining space */
        ActualCopy = StubPtr->Data.Buff.TotalSize - StubPtr->Data.Buff.Position;

        if (log != NULL)
        {
            fprintf(log, "  Computed ActualCopy = TotalSize - Position = %llu\n", (unsigned long long)ActualCopy);
        }

        if (MaxSize < ActualCopy)
        {
            if (log != NULL)
            {
                fprintf(log, "  MaxSize (%llu) < ActualCopy (%llu) => ActualCopy = MaxSize\n",
                        (unsigned long long)MaxSize,
                        (unsigned long long)ActualCopy);
            }
            ActualCopy = MaxSize;
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "  MaxSize (%llu) >= ActualCopy (%llu) => keep ActualCopy\n",
                        (unsigned long long)MaxSize,
                        (unsigned long long)ActualCopy);
            }
        }

        if (log != NULL)
        {
            void *dst = &StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position];
            fprintf(log, "  About to memcpy: dst=%p, src=%p, len=%llu\n",
                    dst,
                    LocalBuffer,
                    (unsigned long long)ActualCopy);
            fflush(log);
        }

        /* Perform the copy exactly as in the original implementation */
        memcpy(&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], LocalBuffer, ActualCopy);

        if (log != NULL)
        {
            fprintf(log, "  memcpy completed\n");
        }

        StubPtr->Data.Buff.Position += ActualCopy;

        if (log != NULL)
        {
            fprintf(log, "  After copy: New Position=%llu\n", (unsigned long long)StubPtr->Data.Buff.Position);
        }

        /* Once the buffer is completely copied then drop this entry. */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (log != NULL)
            {
                fprintf(log, "  Position >= TotalSize -> calling UT_ClearStubEntry(%p)\n", (void *)StubPtr);
            }
            UT_ClearStubEntry(StubPtr);
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "  Position < TotalSize -> leaving entry in table\n");
            }
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  StubPtr is NULL -> no copy performed\n");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: UT_Stub_CopyFromLocal returning ActualCopy=%llu\n\n", (unsigned long long)ActualCopy);
        fclose(log);
    }

    return ActualCopy;
}
