size_t UT_Stub_CopyFromLocal(UT_EntryKey_t FuncKey, const void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/1233.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyFromLocal: entered\n");
        fflush(log);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (log != NULL)
    {
        fprintf(log, "  UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr != NULL)
    {
        size_t total = StubPtr->Data.Buff.TotalSize;
        size_t pos   = StubPtr->Data.Buff.Position;

        if (log != NULL)
        {
            fprintf(log, "  Before compute: TotalSize=%zu, Position=%zu, MaxSize=%zu\n", total, pos, MaxSize);
            fflush(log);
        }

        /* Preserve original computation exactly (note: may underflow if Position > TotalSize) */
        ActualCopy = StubPtr->Data.Buff.TotalSize - StubPtr->Data.Buff.Position;

        if (log != NULL)
        {
            fprintf(log, "  After subtraction: ActualCopy=%zu\n", ActualCopy);
            fflush(log);
        }

        if (MaxSize < ActualCopy)
        {
            if (log != NULL)
            {
                fprintf(log, "  MaxSize (%zu) < ActualCopy (%zu) -> capping ActualCopy to MaxSize\n", MaxSize, ActualCopy);
                fflush(log);
            }
            ActualCopy = MaxSize;
        }

        if (log != NULL)
        {
            void *dest = (void *)&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position];
            fprintf(log, "  About to memcpy: dest=%p, src=%p, size=%zu\n", dest, LocalBuffer, ActualCopy);
            fflush(log);
        }

        memcpy(&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], LocalBuffer, ActualCopy);
        StubPtr->Data.Buff.Position += ActualCopy;

        if (log != NULL)
        {
            fprintf(log, "  After memcpy: New Position=%zu, TotalSize=%zu\n", StubPtr->Data.Buff.Position, StubPtr->Data.Buff.TotalSize);
            fflush(log);
        }

        /*
         * Once the buffer is completely copied then drop this entry.
         * The test harness will either refill it or there might be
         * another entry in the table.
         */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (log != NULL)
            {
                fprintf(log, "  Position >= TotalSize -> clearing stub entry\n");
                fflush(log);
            }

            UT_ClearStubEntry(StubPtr);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  StubPtr is NULL, nothing to do\n");
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyFromLocal: returning ActualCopy=%zu\n\n", ActualCopy);
        fclose(log);
    }

    return ActualCopy;
}
