size_t UT_Stub_CopyToLocal(UT_EntryKey_t FuncKey, void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/2018.txt", "a");

    if (log != NULL)
    {
        /* Minimal entry log so tests show the function was reached */
        fprintf(log, "UT_Stub_CopyToLocal ENTER: FuncKey=%lu LocalBuffer=%p MaxSize=%zu\n",
                (unsigned long)FuncKey, LocalBuffer, MaxSize);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyToLocal: StubPtr=%p\n", (void *)StubPtr);
    }

    if (StubPtr != NULL)
    {
        size_t pos   = StubPtr->Data.Buff.Position;
        size_t total = StubPtr->Data.Buff.TotalSize;

        if (log != NULL)
        {
            fprintf(log, "Buffer state before copy: Position=%zu TotalSize=%zu\n", pos, total);
        }

        ActualCopy = total - pos;

        if (log != NULL)
        {
            fprintf(log, "Calculated ActualCopy (unclipped) = %zu\n", ActualCopy);
        }

        if (MaxSize < ActualCopy)
        {
            ActualCopy = MaxSize;
            if (log != NULL)
            {
                fprintf(log, "Clipped ActualCopy to MaxSize: %zu\n", ActualCopy);
            }
        }

        if (log != NULL)
        {
            fprintf(log, "About to call memcpy: dest=%p src=%p src_index=%zu size=%zu\n",
                    LocalBuffer,
                    (void *)&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position],
                    StubPtr->Data.Buff.Position,
                    ActualCopy);
        }

        /* Preserve original behavior: call memcpy even if ActualCopy == 0 */
        memcpy(LocalBuffer, &StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], ActualCopy);

        if (log != NULL)
        {
            fprintf(log, "memcpy returned; transferred %zu bytes\n", ActualCopy);
        }

        StubPtr->Data.Buff.Position += ActualCopy;

        if (log != NULL)
        {
            fprintf(log, "Updated Position = %zu\n", StubPtr->Data.Buff.Position);
        }

        /* Once the buffer is completely copied then drop this entry. */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (log != NULL)
            {
                fprintf(log, "Position >= TotalSize (%zu >= %zu): clearing stub entry\n",
                        StubPtr->Data.Buff.Position, StubPtr->Data.Buff.TotalSize);
            }
            UT_ClearStubEntry(StubPtr);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyToLocal EXIT: ActualCopy=%zu\n\n", ActualCopy);
        fclose(log);
    }

    return ActualCopy;
}
