size_t UT_Stub_CopyToLocal(UT_EntryKey_t FuncKey, void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/2013.txt", "a");

    if (log != NULL)
    {
        /* Log function entry and basic parameters */
        fprintf(log, "UT_Stub_CopyToLocal ENTER: FuncKey=%lu, LocalBuffer=%p, MaxSize=%zu\n",
                (unsigned long)FuncKey, LocalBuffer, MaxSize);
        fflush(log);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (log != NULL)
    {
        fprintf(log, "UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr != NULL)
    {
        /* Compute how much data remains in the stub buffer (may underflow) */
        ActualCopy = StubPtr->Data.Buff.TotalSize - StubPtr->Data.Buff.Position;

        if (log != NULL)
        {
            fprintf(log, "Computed raw ActualCopy = TotalSize(%zu) - Position(%zu) = %zu\n",
                    StubPtr->Data.Buff.TotalSize,
                    StubPtr->Data.Buff.Position,
                    ActualCopy);
            if (StubPtr->Data.Buff.Position > StubPtr->Data.Buff.TotalSize)
            {
                fprintf(log, "WARNING: Position (%zu) > TotalSize (%zu) -> underflow/wrap occurred, raw ActualCopy=%zu\n",
                        StubPtr->Data.Buff.Position,
                        StubPtr->Data.Buff.TotalSize,
                        ActualCopy);
            }
            fprintf(log, "BasePtr=%p\n", (void *)StubPtr->Data.Buff.BasePtr);
            fflush(log);
        }

        if (MaxSize < ActualCopy)
        {
            if (log != NULL)
            {
                fprintf(log, "Clamping ActualCopy from %zu to MaxSize=%zu\n", ActualCopy, MaxSize);
                fflush(log);
            }
            ActualCopy = MaxSize;
        }

        if (log != NULL)
        {
            void *srcPtr = (void *)&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position];
            fprintf(log, "About to memcpy: dest=%p, src=%p (BasePtr + Position), size=%zu\n",
                    LocalBuffer, srcPtr, ActualCopy);
            /* Provide an explicit note when srcPtr is likely out-of-bounds */
            if (StubPtr->Data.Buff.Position > StubPtr->Data.Buff.TotalSize)
            {
                fprintf(log, "NOTE: srcPtr points past end of buffer: Position(%zu) > TotalSize(%zu)\n",
                        StubPtr->Data.Buff.Position, StubPtr->Data.Buff.TotalSize);
            }
            fflush(log);
        }

        /* Preserve original behavior: perform the copy and advance the position */
        memcpy(LocalBuffer, &StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], ActualCopy);
        StubPtr->Data.Buff.Position += ActualCopy;

        if (log != NULL)
        {
            fprintf(log, "After memcpy: Position updated to %zu\n", StubPtr->Data.Buff.Position);
            fflush(log);
        }

        /* If we've consumed the buffer, clear the entry as before */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (log != NULL)
            {
                fprintf(log, "Buffer fully copied (Position=%zu >= TotalSize=%zu). Calling UT_ClearStubEntry(%p)\n",
                        StubPtr->Data.Buff.Position,
                        StubPtr->Data.Buff.TotalSize,
                        (void *)StubPtr);
                fflush(log);
            }
            UT_ClearStubEntry(StubPtr);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "No stub entry found for FuncKey=%lu\n", (unsigned long)FuncKey);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyToLocal EXIT: ActualCopy=%zu\n\n", ActualCopy);
        fclose(log);
    }

    return ActualCopy;
}
