size_t UT_Stub_CopyToLocal(UT_EntryKey_t FuncKey, void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/2013.txt", "a");

    if (log != NULL)
    {
        /* Log entry to help reproduce control flow and state */
        fprintf(log, "UT_Stub_CopyToLocal ENTER: FuncKey=%zu, LocalBuffer=%p, MaxSize=%zu\n", (size_t)FuncKey, LocalBuffer, MaxSize);
        fflush(log);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (log != NULL)
    {
        fprintf(log, "UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr != NULL)
    {
        if (log != NULL)
        {
            /* Log buffer metadata before computation */
            fprintf(log, "Buffer metadata: BasePtr=%p, TotalSize=%zu, Position=%zu\n",
                    (void *)StubPtr->Data.Buff.BasePtr,
                    StubPtr->Data.Buff.TotalSize,
                    StubPtr->Data.Buff.Position);
            fflush(log);
        }

        /* Original computation (keeps the potential wrap/underflow behavior) */
        ActualCopy = StubPtr->Data.Buff.TotalSize - StubPtr->Data.Buff.Position;

        if (log != NULL)
        {
            fprintf(log, "Computed ActualCopy (before clamp) = %zu\n", ActualCopy);
            fflush(log);
        }

        if (MaxSize < ActualCopy)
        {
            ActualCopy = MaxSize;
            if (log != NULL)
            {
                fprintf(log, "Clamped ActualCopy to MaxSize: %zu\n", ActualCopy);
                fflush(log);
            }
        }

        if (log != NULL)
        {
            /* Compute and log source pointer and planned copy size. This reproduces the same pointer expression used in memcpy. */
            void *src = (void *)&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position];
            fprintf(log, "About to memcpy: src=%p, dst=%p, len=%zu\n", src, LocalBuffer, ActualCopy);
            fflush(log);
        }

        /* Perform the copy exactly as original implementation */
        memcpy(LocalBuffer, &StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], ActualCopy);
        StubPtr->Data.Buff.Position += ActualCopy;

        if (log != NULL)
        {
            fprintf(log, "After memcpy: New Position=%zu\n", StubPtr->Data.Buff.Position);
            fflush(log);
        }

        /* Preserve original behavior: clear entry when fully copied */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (log != NULL)
            {
                fprintf(log, "Position >= TotalSize, clearing stub entry\n");
                fflush(log);
            }
            UT_ClearStubEntry(StubPtr);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "No stub entry found for FuncKey=%zu\n", (size_t)FuncKey);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_Stub_CopyToLocal EXIT: ActualCopy=%zu\n\n", ActualCopy);
        fclose(log);
    }

    return ActualCopy;
}
