size_t UT_Stub_CopyToLocal(UT_EntryKey_t FuncKey, void *LocalBuffer, size_t MaxSize)
{
    size_t               ActualCopy;
    UT_StubTableEntry_t *StubPtr;
    FILE *logf = fopen("/tmp/logs/2015.txt", "a");

    /* Minimal entry log to prove reachability */
    if (logf != NULL)
    {
        fprintf(logf, "Entered UT_Stub_CopyToLocal: FuncKey=%lu LocalBuffer=%p MaxSize=%zu\n",
                (unsigned long)FuncKey, (void *)LocalBuffer, MaxSize);
    }

    ActualCopy = 0;
    StubPtr    = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_DATA_BUFFER);

    if (StubPtr != NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "Found StubPtr=%p TotalSize=%zu Position=%zu\n",
                    (void *)StubPtr,
                    (size_t)StubPtr->Data.Buff.TotalSize,
                    (size_t)StubPtr->Data.Buff.Position);
        }

        ActualCopy = StubPtr->Data.Buff.TotalSize - StubPtr->Data.Buff.Position;
        if (MaxSize < ActualCopy)
        {
            ActualCopy = MaxSize;
        }

        if (logf != NULL)
        {
            void *src_ptr = (void *)&StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position];
            fprintf(logf, "Computed ActualCopy=%zu src_ptr=%p (BasePtr+Position)\n", ActualCopy, src_ptr);

            /* Log a small sample of the source bytes (up to 16) to capture state without risking large output) */
            {
                size_t sample = (ActualCopy < 16U) ? ActualCopy : 16U;
                unsigned char *s = (unsigned char *)src_ptr;

                if (sample > 0U)
                {
                    fprintf(logf, "Source sample (%zu bytes):", sample);
                    for (size_t i = 0U; i < sample; ++i)
                    {
                        /* Print as two-digit hex */
                        fprintf(logf, " %02X", (unsigned)s[i]);
                    }
                    fprintf(logf, "\n");
                }
                else
                {
                    fprintf(logf, "Source sample: <empty>\n");
                }
            }
        }

        /* Preserve original behavior: copy into LocalBuffer and advance position */
        memcpy(LocalBuffer, &StubPtr->Data.Buff.BasePtr[StubPtr->Data.Buff.Position], ActualCopy);

        if (logf != NULL)
        {
            fprintf(logf, "Performed memcpy to LocalBuffer=%p length=%zu\n", LocalBuffer, ActualCopy);
        }

        StubPtr->Data.Buff.Position += ActualCopy;

        /*
         * Once the buffer is completely copied then drop this entry.
         * The test harness will either refill it or there might be
         * another entry in the table.
         */
        if (StubPtr->Data.Buff.Position >= StubPtr->Data.Buff.TotalSize)
        {
            if (logf != NULL)
            {
                fprintf(logf, "Buffer fully consumed: new Position=%zu >= TotalSize=%zu. Clearing stub entry.\n",
                        (size_t)StubPtr->Data.Buff.Position,
                        (size_t)StubPtr->Data.Buff.TotalSize);
            }

            UT_ClearStubEntry(StubPtr);
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "No stub entry found for FuncKey=%lu\n", (unsigned long)FuncKey);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "Exiting UT_Stub_CopyToLocal: ActualCopy=%zu\n", ActualCopy);
        fclose(logf);
    }

    return ActualCopy;
}
