void UT_Stub_CopyToReturnValue(UT_EntryKey_t FuncKey, const void *BufferPtr, size_t BufferSize)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *logf = fopen("/tmp/logs/1613.txt", "a");

    if (logf != NULL)
    {
        fprintf(logf, "ENTER UT_Stub_CopyToReturnValue: FuncKey=%llu, BufferPtr=%p, BufferSize=%zu\n",
                (unsigned long long)FuncKey, BufferPtr, BufferSize);
        fflush(logf);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);

    if (logf != NULL)
    {
        if (StubPtr != NULL)
        {
            fprintf(logf, "  Found StubPtr=%p, TotalSize=%zu, BasePtr=%p, Position=%zu\n",
                    (void *)StubPtr,
                    (size_t)StubPtr->Data.Buff.TotalSize,
                    (void *)StubPtr->Data.Buff.BasePtr,
                    (size_t)StubPtr->Data.Buff.Position);
        }
        else
        {
            fprintf(logf, "  No stub entry found for FuncKey=%llu (UT_GetStubEntry returned NULL)\n",
                    (unsigned long long)FuncKey);
        }
        fflush(logf);
    }

    if (StubPtr != NULL)
    {
        /* If the size does not match, there is a bug */
        if (StubPtr->Data.Buff.TotalSize != BufferSize)
        {
            if (logf != NULL)
            {
                fprintf(logf, "  Size mismatch: Stub TotalSize=%zu, Provided BufferSize=%zu -- aborting\n",
                        (size_t)StubPtr->Data.Buff.TotalSize, BufferSize);
                fflush(logf);
                fclose(logf);
                logf = NULL;
            }
            UtAssert_Abort("Size mismatch in setting return value");
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "  Performing memcpy: dest=%p, src=%p, n=%zu\n",
                        (void *)StubPtr->Data.Buff.BasePtr, BufferPtr, BufferSize);
                fflush(logf);
            }

            memcpy(StubPtr->Data.Buff.BasePtr, BufferPtr, BufferSize);
            StubPtr->Data.Buff.Position = BufferSize;

            if (logf != NULL)
            {
                fprintf(logf, "  memcpy complete: New Position=%zu\n", (size_t)StubPtr->Data.Buff.Position);
                fflush(logf);
            }
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "EXIT UT_Stub_CopyToReturnValue\n");
        fclose(logf);
    }
}
