void UT_Stub_CopyToReturnValue(UT_EntryKey_t FuncKey, const void *BufferPtr, size_t BufferSize)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/1613.txt", "a");

    if (log)
    {
        /* Log entry and basic parameters */
        fprintf(log, "UT_Stub_CopyToReturnValue ENTRY: FuncKey=%lu, BufferPtr=%p, BufferSize=%zu\n",
                (unsigned long)FuncKey, (void *)BufferPtr, BufferSize);
        fflush(log);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);

    if (log)
    {
        fprintf(log, "  UT_GetStubEntry returned StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr != NULL)
    {
        if (log)
        {
            /* Log the buffer metadata available in the stub */
            fprintf(log, "  Stub Buffer TotalSize=%zu, BasePtr=%p, Position=%zu\n",
                    StubPtr->Data.Buff.TotalSize,
                    (void *)StubPtr->Data.Buff.BasePtr,
                    StubPtr->Data.Buff.Position);
            fflush(log);
        }

        /* If the size does not match, there is a bug */
        if (StubPtr->Data.Buff.TotalSize != BufferSize)
        {
            if (log)
            {
                fprintf(log, "  SIZE MISMATCH: StubTotal=%zu != BufferSize=%zu -> UtAssert_Abort()\n",
                        StubPtr->Data.Buff.TotalSize, BufferSize);
                fclose(log);
            }

            UtAssert_Abort("Size mismatch in setting return value");
        }
        else
        {
            if (log)
            {
                /* Log right before the potentially unsafe memcpy to capture pointers and size */
                fprintf(log, "  PERFORM memcpy: dest=%p <= src=%p, size=%zu\n",
                        (void *)StubPtr->Data.Buff.BasePtr, (void *)BufferPtr, BufferSize);
                fflush(log);
            }

            /* Preserve original behavior: this may read from BufferPtr without additional validation */
            memcpy(StubPtr->Data.Buff.BasePtr, BufferPtr, BufferSize);
            StubPtr->Data.Buff.Position = BufferSize;

            if (log)
            {
                fprintf(log, "  memcpy completed. New Position=%zu\n", StubPtr->Data.Buff.Position);
                fclose(log);
            }
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "  No stub entry found (StubPtr == NULL). No action performed.\n");
            fclose(log);
        }
    }
}
