void *UT_Stub_GetReturnValuePtr(UT_EntryKey_t FuncKey, size_t ReturnSize, const char *TypeName)
{
    UT_StubTableEntry_t *StubPtr;
    void *               ReturnPtr;

    /* Logging probes to capture control flow and key state. Try to open the log file
     * in append mode. If opening fails, continue without logging so we don't
     * change the original control flow or outcome. Do not dereference any
     * potentially-NULL string pointers when logging to avoid altering behavior.
     */
    FILE *logf = fopen("/tmp/logs/1733.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "ENTER: UT_Stub_GetReturnValuePtr: FuncKey=%lu, ReturnSize=%lu, TypeName_ptr=%p\n",
                (unsigned long)FuncKey, (unsigned long)ReturnSize, (const void *)TypeName);
    }

    ReturnPtr = NULL;
    StubPtr   = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);

    if (logf != NULL)
    {
        fprintf(logf, "UT_GetStubEntry returned StubPtr=%p\n", (const void *)StubPtr);
        fflush(logf);
    }

    if (StubPtr == NULL)
    {
        /* This shouldn't happen, it means the stub tried to use a
         * return buffer that does not exist.
         *
         * It is most likely caused by a mismatch/incompatibility between
         * stub and handler.  Aborting now is better than segfaulting later,
         * as the errored call should still be on the stack trace
         */
        if (logf != NULL)
        {
            fprintf(logf, "ERROR: StubPtr is NULL -> UtAssert_Abort will be invoked\n");
            fclose(logf);
        }

        UtAssert_Abort("Return buffer invalid");
    }
    else
    {
        ReturnPtr = StubPtr->Data.Buff.BasePtr;

        if (logf != NULL)
        {
            fprintf(logf, "Stub details: BasePtr=%p, Position=%lu, ContentTypeStr_ptr=%p\n",
                    StubPtr->Data.Buff.BasePtr, (unsigned long)StubPtr->Data.Buff.Position,
                    (const void *)StubPtr->Data.Buff.ContentTypeStr);
            fflush(logf);
        }

        /*
         * Sanity check on the size and type name.
         *
         * In a generated stub these should always match, but if any patches
         * were done or if a hand-written stub is used, mistakes could be made.
         */
        /* Preserve original behavior: perform the same comparisons (including
         * the strcmp call) so that outcomes remain identical.  Log the
         * comparison about to occur (pointers only) so that reproducer agents
         * can see the inputs leading up to any failure or crash.
         */
        if (logf != NULL)
        {
            fprintf(logf, "About to compare: Position(%lu) vs ReturnSize(%lu) and strings at %p and %p\n",
                    (unsigned long)StubPtr->Data.Buff.Position, (unsigned long)ReturnSize,
                    (const void *)TypeName, (const void *)StubPtr->Data.Buff.ContentTypeStr);
            fflush(logf);
        }

        /* Perform the exact same check as the original code. Note that this
         * will invoke strcmp exactly once as before; if a NULL or invalid
         * pointer is provided by the caller this may still fault here, which
         * is the behavior we must preserve for accurate reproduction.
         */
        int cmpres = 0;
        /* The strcmp call is intentionally executed as in the original code to
         * preserve behavior. */
        cmpres = strcmp(TypeName, StubPtr->Data.Buff.ContentTypeStr);

        if (logf != NULL)
        {
            fprintf(logf, "Comparison result: Position_match=%s, strcmp_result=%d\n",
                    (StubPtr->Data.Buff.Position == ReturnSize) ? "yes" : "no", cmpres);
            fflush(logf);
        }

        if (StubPtr->Data.Buff.Position != ReturnSize || cmpres != 0)
        {
            if (logf != NULL)
            {
                fprintf(logf, "UtAssert_Failed will be invoked: expected %p(%lu) got %p(%lu)\n",
                        (const void *)TypeName, (unsigned long)ReturnSize,
                        (const void *)StubPtr->Data.Buff.ContentTypeStr,
                        (unsigned long)StubPtr->Data.Buff.Position);
                fclose(logf);
            }

            UtAssert_Failed("Return value mismatch, expected %s(%lu) got %s(%lu)", TypeName,
                            (unsigned long)ReturnSize, StubPtr->Data.Buff.ContentTypeStr,
                            (unsigned long)StubPtr->Data.Buff.Position);
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "Return value match OK, returning BasePtr=%p\n", ReturnPtr);
                fclose(logf);
            }
        }
    }

    return ReturnPtr;
}
