bool UT_Stub_IsValueCompatible(const UT_RetvalConfigEntry_t *Rvc, const char *WantTypeName, UT_ValueGenre_t WantGenre)
{
    bool IsCompatible;

    /* Open log file for append so multiple calls accumulate evidence */
    FILE *log = fopen("/tmp/logs/165.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER UT_Stub_IsValueCompatible: Rvc=%p, Rvc->TypeName=%p, WantTypeName=%p, WantGenre=%d\n",
                (void *)Rvc,
                (void *)(Rvc ? Rvc->TypeName : NULL),
                (void *)WantTypeName,
                (int)WantGenre);
    }

    /* If the type names were specified on both sides, and they match, then the value is compatible */
    if (Rvc->TypeName != NULL && WantTypeName != NULL)
    {
        int cmp = strcmp(Rvc->TypeName, WantTypeName);
        IsCompatible = (cmp == 0);

        if (log != NULL)
        {
            /* Print the actual strings and comparison result to capture evidence */
            fprintf(log, "TypeName compare: Rvc->TypeName=\"%s\", WantTypeName=\"%s\" => strcmp=%d, IsCompatible=%d\n",
                    Rvc->TypeName, WantTypeName, cmp, IsCompatible);
        }
    }
    else
    {
        /* The types are not _directly_ assignable, but it could possibly be coerced */
        IsCompatible = false;
        if (log != NULL)
        {
            if (Rvc->TypeName == NULL)
            {
                fprintf(log, "Rvc->TypeName == NULL\n");
            }
            if (WantTypeName == NULL)
            {
                fprintf(log, "WantTypeName == NULL\n");
            }
            fprintf(log, "Set IsCompatible = false (type names not both specified)\n");
        }
    }

    /*
     * As an alternative/fallback, if the value genre is known for both sides,
     * check if coercion is a possibility.  For example, this could apply if the
     * value was int32 on one side and int16 on the other.
     */
    if (!IsCompatible && Rvc->Genre != UT_ValueGenre_OPAQUE && Rvc->Genre == WantGenre)
    {
        /* Genre match - should be compatible, with a potential size adjustment */
        IsCompatible = true;
        if (log != NULL)
        {
            fprintf(log, "Genre fallback taken: Rvc->Genre=%d matches WantGenre=%d => IsCompatible=true\n",
                    (int)Rvc->Genre, (int)WantGenre);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "Genre check: Rvc->Genre=%d, WantGenre=%d, IsCompatible=%d\n",
                    (int)Rvc->Genre, (int)WantGenre, IsCompatible);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT UT_Stub_IsValueCompatible: result=%d\n\n", IsCompatible);
        fclose(log);
    }

    return IsCompatible;
}
