bool UtAssertEx(bool Expression, UtAssert_CaseType_t CaseType, const char *File, uint32 Line, const char *MessageFormat,
                ...)
{
    va_list va;
    char    FinalMessage[256];
    uint32  TestSegmentCount;
    uint32  TotalTestCases;

    /* Logging probe file */
    FILE *logf = fopen("/tmp/logs/1966.txt", "a");
    if (logf)
    {
        fprintf(logf, "[UtAssertEx] ENTER: Expression=%d, initial CaseType=%u, File=%s, Line=%u\n",
                Expression ? 1 : 0, (unsigned)CaseType, File ? File : "(null)", (unsigned)Line);
        fflush(logf);
    }

    UT_BSP_Lock();

    if (logf)
    {
        /* Log values observed while holding the same lock as original code */
        fprintf(logf, "[UtAssertEx] Locked. UT_SegmentCounters.TotalTestCases(before)=%u, UT_TotalCounters.TestSegmentCount=%u\n",
                (unsigned)UT_SegmentCounters.TotalTestCases, (unsigned)UT_TotalCounters.TestSegmentCount);
        fflush(logf);
    }

    ++UT_SegmentCounters.TotalTestCases;

    if (Expression)
    {
        CaseType = UTASSERT_CASETYPE_PASS;
    }

    if ((uint32)CaseType < UTASSERT_CASETYPE_MAX)
    {
        ++UT_SegmentCounters.CaseCount[(uint32)CaseType];
    }

    TestSegmentCount = 1 + UT_TotalCounters.TestSegmentCount;
    TotalTestCases   = UT_SegmentCounters.TotalTestCases;

    if (logf)
    {
        fprintf(logf, "[UtAssertEx] Before unlock: CaseType=%u, TestSegmentCount=%u, TotalTestCases=%u\n",
                (unsigned)CaseType, (unsigned)TestSegmentCount, (unsigned)TotalTestCases);
        fflush(logf);
    }

    UT_BSP_Unlock();

    if (logf)
    {
        fprintf(logf, "[UtAssertEx] Unlocked. CurrentSegment=%p", (void *)CurrentSegment);
        if (CurrentSegment)
        {
            /* Attempt to print CurrentSegment as string if available */
            fprintf(logf, " (string='%s')", CurrentSegment);
        }
        fprintf(logf, "\n");
        fflush(logf);
    }

    va_start(va, MessageFormat);
    vsnprintf(FinalMessage, sizeof(FinalMessage), MessageFormat, va);
    va_end(va);

    if (logf)
    {
        /* Log the final composed message and the final outcome to provide evidence for control flow */
        fprintf(logf, "[UtAssertEx] FinalMessage='%s'\n", FinalMessage[0] ? FinalMessage : "(empty)");
        fprintf(logf, "[UtAssertEx] REPORT: File=%s, Line=%u, TestSegmentCount=%u, TotalTestCases=%u, CaseType=%u\n",
                File ? File : "(null)", (unsigned)Line, (unsigned)TestSegmentCount, (unsigned)TotalTestCases, (unsigned)CaseType);
        fprintf(logf, "[UtAssertEx] EXIT: Returning Expression=%d\n\n", Expression ? 1 : 0);
        fclose(logf);
    }

    UtAssert_DoReport(File, Line, TestSegmentCount, TotalTestCases, CaseType, CurrentSegment, FinalMessage);

    return Expression;
}
