void UtAssert_BeginTest(const char *SegmentName)
{
    uint32 TestSegmentCount;
    FILE *log = fopen("/tmp/logs/636.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "UtAssert_BeginTest: ENTER SegmentName=\"%s\"\n", (SegmentName != NULL) ? SegmentName : "(null)");
        /* Log a best-effort snapshot of current state before modification */
        fprintf(log, "UtAssert_BeginTest: Before CurrentSegment=\"%s\"\n", (CurrentSegment[0] != 0) ? CurrentSegment : "(empty)");
        fprintf(log, "UtAssert_BeginTest: UT_TotalCounters.TestSegmentCount=%u\n", (unsigned)UT_TotalCounters.TestSegmentCount);
        fflush(log);
    }

    UT_BSP_Lock();

    memset(&UT_SegmentCounters, 0, sizeof(UT_SegmentCounters));
    strncpy(CurrentSegment, SegmentName, sizeof(CurrentSegment) - 1);
    CurrentSegment[sizeof(CurrentSegment) - 1] = 0;
    TestSegmentCount                           = 1 + UT_TotalCounters.TestSegmentCount;

    if (log != NULL)
    {
        fprintf(log, "UtAssert_BeginTest: After strncpy CurrentSegment=\"%s\"\n", CurrentSegment);
        fprintf(log, "UtAssert_BeginTest: Computed TestSegmentCount=%u\n", (unsigned)TestSegmentCount);
        fflush(log);
    }

    UT_BSP_Unlock();

    if (log != NULL)
    {
        fprintf(log, "UtAssert_BeginTest: After UT_BSP_Unlock\n");
        fflush(log);
        fclose(log);
    }

    UT_BSP_StartTestSegment(TestSegmentCount, SegmentName);
}
