void UtAssert_DoReport(const char *File, uint32 LineNum, uint32 SegmentNum, uint32 TestSeq, uint8 MessageType,
                       const char *SubsysName, const char *ShortDesc)
{
    uint32      FileLen;
    const char *BasePtr;
    char        ReportBuffer[320];

    /* Probe logging: write control-flow / state info to a persistent log before continuing.
     * This helps capture the scenario where File == NULL (or not NUL-terminated) prior to
     * invoking strlen(File). If File is NULL, the subsequent strlen(File) will exhibit the
     * original undefined behaviour; the log is flushed to disk beforehand so the scenario
     * can be diagnosed even if a crash follows. */
    {
        FILE *log = fopen("/tmp/logs/1263.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UtAssert_DoReport ENTERED\n");
            fprintf(log, "  File ptr     : %p\n", (const void *)File);
            fprintf(log, "  LineNum      : %u\n", (unsigned int)LineNum);
            fprintf(log, "  SegmentNum   : %u\n", (unsigned int)SegmentNum);
            fprintf(log, "  TestSeq      : %u\n", (unsigned int)TestSeq);
            fprintf(log, "  MessageType  : %u\n", (unsigned int)MessageType);
            fprintf(log, "  SubsysName ptr: %p\n", (const void *)SubsysName);
            fprintf(log, "  ShortDesc ptr : %p\n", (const void *)ShortDesc);

            if (SubsysName != NULL)
            {
                /* Print up to a reasonable amount to avoid huge logs; original behavior is not changed. */
                fprintf(log, "  SubsysName   : '");
                /* Use %s only when non-NULL to avoid further UB. */
                fprintf(log, "%s", SubsysName);
                fprintf(log, "'\n");
            }
            else
            {
                fprintf(log, "  SubsysName   : (NULL)\n");
            }

            if (ShortDesc != NULL)
            {
                fprintf(log, "  ShortDesc    : '");
                fprintf(log, "%s", ShortDesc);
                fprintf(log, "'\n");
            }
            else
            {
                fprintf(log, "  ShortDesc    : (NULL)\n");
            }

            /* Ensure the log is written immediately to help post-mortem analysis if a crash occurs. */
            fflush(log);
            fclose(log);
        }
    }

    /* Original logic preserved as closely as possible. Note: if File == NULL this will
     * still call strlen(File) and may crash, which is intended for reproducing the reported UB. */
    FileLen = strlen(File);
    BasePtr = File + FileLen;
    while (FileLen > 0)
    {
        --BasePtr;
        --FileLen;
        if (*BasePtr == '/' || *BasePtr == '\\')
        {
            ++BasePtr;
            break;
        }
    }

    snprintf(ReportBuffer, sizeof(ReportBuffer), "%02u.%03u %s:%u - %s", (unsigned int)SegmentNum,
             (unsigned int)TestSeq, BasePtr, (unsigned int)LineNum, ShortDesc);

    UT_BSP_DoText(MessageType, ReportBuffer);
}
