bool UtAssert_GenericIntegerCompare(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                    UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                    const char *Typename, const char *ActualText, const char *RefText)
{
    static const char UTASSERT_PREFIX[] = "UTASSERT_";

    char ActualStr[32];
    char RefStr[32];
    char TagStr[32];
    int  TagLen;

    /* Open a log file to record control flow and state for diagnosis */
    FILE *log = fopen("/tmp/logs/1012.txt", "a");
    if (log)
    {
        fprintf(log, "Entered UtAssert_GenericIntegerCompare: File=%s Line=%u IsUnsigned=%d CompareType=%d RadixType=%d Typename=%s ActualText=%s RefText=%s AddrActual=%p AddrRef=%p\n",
                (File ? File : "(null)"), (unsigned)Line, (IsUnsigned ? 1 : 0), (int)CompareType, (int)RadixType,
                (Typename ? Typename : "(null)"), (ActualText ? ActualText : "(null)"), (RefText ? RefText : "(null)"),
                (void *)ActualText, (void *)RefText);
    }

    /* If the radix type was not specified, then check if the typename appears to be a pointer -
     * That is, it contains an asterisk.  This is far from foolproof due to typedefs etc but
     * it should catch most of them (note that "hiding" a pointer via typedef is discouraged by
     * GSFC coding standards, so this shouldn't be too likely)
     */
    if (Typename != NULL && *Typename != 0)
    {
        TagLen = snprintf(TagStr, sizeof(TagStr), "%s", Typename);
        if (TagLen < 0)
        {
            TagLen = 0;
        }
        else if (TagLen > (sizeof(TagStr) - 3))
        {
            TagLen = sizeof(TagStr) - 3;
        }

        while (TagLen > 0 && (isspace((unsigned char)TagStr[TagLen - 1]) || TagStr[TagLen - 1] == ':'))
        {
            --TagLen;
        }

        if (TagLen > 0)
        {
            TagStr[TagLen] = ':';
            ++TagLen;
            TagStr[TagLen] = ' ';
            ++TagLen;
        }

        TagStr[TagLen] = 0;

        if (RadixType == UtAssert_Radix_DEFAULT && strchr(Typename, '*') != NULL)
        {
            /* looks like a pointer type */
            RadixType = UtAssert_Radix_HEX;
            if (log)
            {
                fprintf(log, "Typename contains '*', setting RadixType to HEX\n");
            }
        }
    }
    else
    {
        TagStr[0] = 0;
    }

    /* If either the actual text or the ref text starts with the "UTASSERT_" prefix, then strip it */
    if (strncmp(ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        if (log)
        {
            fprintf(log, "ActualText prefix matched. Addr before=%p TextBefore=\"%s\"\n", (void *)ActualText, ActualText);
        }
        ActualText += sizeof(UTASSERT_PREFIX) - 1;
        if (log)
        {
            fprintf(log, "ActualText adjusted. Addr after=%p TextAfter=\"%s\"\n", (void *)ActualText, ActualText);
        }
    }
    if (strncmp(RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        /* Violation scenario: pointer parameter RefText is reassigned here */
        if (log)
        {
            fprintf(log, "RefText prefix matched. Addr before=%p TextBefore=\"%s\"\n", (void *)RefText, RefText);
        }
        RefText += sizeof(UTASSERT_PREFIX) - 1;
        if (log)
        {
            fprintf(log, "RefText adjusted. Addr after=%p TextAfter=\"%s\"\n", (void *)RefText, RefText);
        }
    }

    /* Perform the original operation and capture the result for logging */
    bool result = UtAssertEx(UtAssert_DoCompare(ActualValue, CompareType, RefValue, IsUnsigned), UTASSERT_CASETYPE_FAILURE,
                      File, Line, "%s%s (%s) %s %s (%s)", TagStr, ActualText,
                      UtAssert_GetValueText(ActualStr, sizeof(ActualStr), ActualValue, IsUnsigned, RadixType),
                      UtAssert_GetOpText(CompareType), RefText,
                      UtAssert_GetValueText(RefStr, sizeof(RefStr), RefValue, IsUnsigned, RadixType));

    if (log)
    {
        fprintf(log, "Exiting UtAssert_GenericIntegerCompare: result=%d FinalActualTextAddr=%p FinalRefTextAddr=%p\n",
                (int)result, (void *)ActualText, (void *)RefText);
        fclose(log);
    }

    return result;
}
