bool UtAssert_GenericIntegerCompare(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                    UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                    const char *Typename, const char *ActualText, const char *RefText)
{
    static const char UTASSERT_PREFIX[] = "UTASSERT_";

    char ActualStr[32];
    char RefStr[32];
    char TagStr[32];
    int  TagLen;

    /* Logging probe: open log file and record entry and pointer state. Do not dereference ActualText/RefText. */
    FILE *log = fopen("/tmp/logs/1013.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER UtAssert_GenericIntegerCompare: File=%s Line=%u\n", File ? File : "(null)", (unsigned)Line);
        fprintf(log, "  IsUnsigned=%d CompareType=%d RadixType=%d\n", (int)IsUnsigned, (int)CompareType, (int)RadixType);
        fprintf(log, "  Typename ptr=%p ActualText ptr=%p RefText ptr=%p\n", (const void *)Typename, (const void *)ActualText, (const void *)RefText);
        fprintf(log, "  UTASSERT_PREFIX='%s' prefix_len=%zu\n", UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1);
        fflush(log);
    }

    /* If the radix type was not specified, then check if the typename appears to be a pointer -
     * That is, it contains an asterisk.  This is far from foolproof due to typedefs etc but
     * it should catch most of them (note that "hiding" a pointer via typedef is discouraged by
     * GSFC coding standards, so this shouldn't be too likely)
     */
    if (Typename != NULL && *Typename != 0)
    {
        TagLen = snprintf(TagStr, sizeof(TagStr), "%s", Typename);
        if (TagLen < 0)
        {
            TagLen = 0;
        }
        else if (TagLen > (sizeof(TagStr) - 3))
        {
            TagLen = sizeof(TagStr) - 3;
        }

        while (TagLen > 0 && (isspace((unsigned char)TagStr[TagLen - 1]) || TagStr[TagLen - 1] == ':'))
        {
            --TagLen;
        }

        if (TagLen > 0)
        {
            TagStr[TagLen] = ':';
            ++TagLen;
            TagStr[TagLen] = ' ';
            ++TagLen;
        }

        TagStr[TagLen] = 0;

        if (RadixType == UtAssert_Radix_DEFAULT && strchr(Typename, '*') != NULL)
        {
            /* looks like a pointer type */
            RadixType = UtAssert_Radix_HEX;
        }

        if (log != NULL)
        {
            fprintf(log, "  After Typename handling: TagStr='%s' TagLen=%d RadixType=%d\n", TagStr, TagLen, (int)RadixType);
            fflush(log);
        }
    }
    else
    {
        TagStr[0] = 0;
        if (log != NULL)
        {
            fprintf(log, "  No Typename provided.\n");
            fflush(log);
        }
    }

    /* If either the actual text or the ref text starts with the "UTASSERT_" prefix, then strip it */
    if (log != NULL)
    {
        fprintf(log, "  About to check ActualText prefix at ptr=%p\n", (const void *)ActualText);
        fflush(log);
    }
    if (strncmp(ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        ActualText += sizeof(UTASSERT_PREFIX) - 1;
        if (log != NULL)
        {
            fprintf(log, "    ActualText matched prefix; adjusted ActualText ptr=%p\n", (const void *)ActualText);
            fflush(log);
        }
    }
    if (log != NULL)
    {
        fprintf(log, "  About to check RefText prefix at ptr=%p\n", (const void *)RefText);
        fflush(log);
    }
    if (strncmp(RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        RefText += sizeof(UTASSERT_PREFIX) - 1;
        if (log != NULL)
        {
            fprintf(log, "    RefText matched prefix; adjusted RefText ptr=%p\n", (const void *)RefText);
            fflush(log);
        }
    }

    /* Call the original comparison/reporting routine and capture its return value so we can log final state */
    bool result = UtAssertEx(UtAssert_DoCompare(ActualValue, CompareType, RefValue, IsUnsigned), UTASSERT_CASETYPE_FAILURE,
                      File, Line, "%s%s (%s) %s %s (%s)", TagStr, ActualText,
                      UtAssert_GetValueText(ActualStr, sizeof(ActualStr), ActualValue, IsUnsigned, RadixType),
                      UtAssert_GetOpText(CompareType), RefText,
                      UtAssert_GetValueText(RefStr, sizeof(RefStr), RefValue, IsUnsigned, RadixType));

    if (log != NULL)
    {
        fprintf(log, "EXIT UtAssert_GenericIntegerCompare: result=%d ActualText ptr=%p RefText ptr=%p\n\n", (int)result, (const void *)ActualText, (const void *)RefText);
        fclose(log);
    }

    return result;
}
