#include <stdio.h>
#include <sys/types.h>
#include <sys/stat.h>

const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;
    int default_taken = 0;
    FILE *logf = fopen("/tmp/logs/2938.txt", "a");

    /* Ensure there's at least an attempt to create the directory if missing */
    if (logf == NULL)
    {
        (void)mkdir("/tmp/logs", 0777);
        logf = fopen("/tmp/logs/2938.txt", "a");
    }

    if (logf != NULL)
    {
        /* Log entry and the raw CaseType value so the control path can be reproduced */
        fprintf(logf, "UtAssert_GetCaseTypeAbbrev: entered with CaseType=%d\n", (int)CaseType);
    }

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_ABORT -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_FAILURE -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_MIR -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_TSF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_TTF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_WARN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_NA -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_BEGIN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_END -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_PASS -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_INFO -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_FLOW -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            if (logf) fprintf(logf, "branch taken: UTASSERT_CASETYPE_DEBUG -> %s\n", AbbrevStr);
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            default_taken = 1;
            if (logf) fprintf(logf, "branch taken: default -> %s (default_taken=%d)\n", AbbrevStr, default_taken);
            break;
    }

    if (logf)
    {
        fprintf(logf, "UtAssert_GetCaseTypeAbbrev: exiting with AbbrevStr=%s\n", AbbrevStr);
        fclose(logf);
    }

    return AbbrevStr;
}
