const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;
    FILE *log = NULL;

    /* Attempt to open the requested log file; if the directory doesn't exist, fall back to /tmp/2933.txt */
    log = fopen("/tmp/logs/2933.txt", "a");
    if (log == NULL)
    {
        /* Fallback: try a file directly under /tmp to increase the chance logs are written */
        log = fopen("/tmp/2933.txt", "a");
    }

    if (log)
    {
        /* Minimal entry indicating function was reached and the input value */
        fprintf(log, "UtAssert_GetCaseTypeAbbrev ENTER: CaseType=%d\n", (int)CaseType);
    }

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_ABORT -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_FAILURE -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_MIR -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_TSF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_TTF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_WARN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_NA -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_BEGIN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_END -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_PASS -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_INFO -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_FLOW -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            if (log) fprintf(log, "  branch: UTASSERT_CASETYPE_DEBUG -> %s\n", AbbrevStr);
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            if (log) fprintf(log, "  branch: DEFAULT -> %s\n", AbbrevStr);
            break;
    }

    if (log)
    {
        /* Log the returned pointer and close the file */
        fprintf(log, "UtAssert_GetCaseTypeAbbrev EXIT: Returning '%s' at %p\n", AbbrevStr, (const void *)AbbrevStr);
        fclose(log);
    }

    return AbbrevStr;
}
