const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;
    FILE *log = fopen("/tmp/logs/2933.txt", "a");

    if (log)
    {
        /* Log entry and the raw numeric case type for reproducibility */
        fprintf(log, "ENTER: UtAssert_GetCaseTypeAbbrev CaseType=%d\n", (int)CaseType);
        fflush(log);
    }

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_ABORT -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_FAILURE -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_MIR -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_TSF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_TTF -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_WARN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_NA -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_BEGIN -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_END -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_PASS -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_INFO -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_FLOW -> %s\n", AbbrevStr);
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            if (log) fprintf(log, "PATH: UTASSERT_CASETYPE_DEBUG -> %s\n", AbbrevStr);
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            if (log) fprintf(log, "PATH: DEFAULT -> %s (CaseType=%d)\n", AbbrevStr, (int)CaseType);
            break;
    }

    if (log)
    {
        /* Final state: pointer and string content */
        fprintf(log, "EXIT: AbbrevStr=%p -> \"%s\"\n\n", (const void *)AbbrevStr, AbbrevStr);
        fclose(log);
    }

    return AbbrevStr;
}
