const char *UtAssert_GetOpText(UtAssert_Compare_t CompareType)
{
    const char *OpText;

    /* Minimal entry probe so the function being reached is logged */
    {
        FILE *log = fopen("/tmp/logs/492.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UtAssert_GetOpText: ENTER CompareType=%d\n", (int)CompareType);
            fclose(log);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            OpText = "==";
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            OpText = "!=";
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            OpText = "<";
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            OpText = ">";
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            OpText = "<=";
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            OpText = ">=";
            break;
        case UtAssert_Compare_BITMASK_SET: /* bit(s) in reference are set in actual */
            OpText = "&";
            break;
        case UtAssert_Compare_BITMASK_UNSET: /* bit(s) in reference are not set in actual */
            OpText = "&~";
            break;
        default: /* should never happen */
            OpText = "??";
            break;
    }

    /* Log the selected operation text and some state info to help reproduce pointer-related issues elsewhere. */
    {
        FILE *log = fopen("/tmp/logs/492.txt", "a");
        if (log != NULL)
        {
            /* Attempt to print the pointer value and the string content (safe because OpText is a literal in this function).
               strlen may be available in the surrounding translation unit; if not, printing the literal is still informative. */
            size_t len = 0;
            const char *p = OpText;
            while (p && *p != '\0') { len++; p++; }

            fprintf(log, "UtAssert_GetOpText: SELECTED OpText=\"%s\" addr=%p len=%zu CompareType=%d\n",
                    OpText, (void *)OpText, len, (int)CompareType);
            fclose(log);
        }
    }

    return OpText;
}
