void UtAssert_Message(uint8 MessageType, const char *File, uint32 Line, const char *Spec, ...)
{
    va_list     va;
    char        FinalMessage[256];
    const char *BaseName;
    size_t      MsgLen;
    FILE       *log = NULL;

    /* Minimal probe: open log file and record entry parameters and caller return addresses */
    log = fopen("/tmp/logs/870.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "--- ENTER UtAssert_Message ---\n");
        fprintf(log, "MessageType=%u, File=%p, Line=%u, Spec=%s\n",
                (unsigned)MessageType, (void *)File, (unsigned)Line, (Spec != NULL) ? Spec : "(null)");

        /* Capture a small call chain (return addresses). Useful to correlate indirect calls. */
        for (int i = 0; i < 4; ++i)
        {
            void *ra = __builtin_return_address(i);
            if (ra == NULL)
            {
                break;
            }
            fprintf(log, "  return_address[%d]=%p\n", i, ra);
        }
        fflush(log);
    }

    if (File != NULL)
    {
        BaseName = strrchr(File, '/');
        if (BaseName == NULL)
        {
            BaseName = File;
        }
        else
        {
            ++BaseName;
        }
        snprintf(FinalMessage, sizeof(FinalMessage), "%s:%u:", BaseName, (unsigned int)Line);
        MsgLen = strlen(FinalMessage);
    }
    else
    {
        MsgLen = 0;
    }

    va_start(va, Spec);
    vsnprintf(&FinalMessage[MsgLen], sizeof(FinalMessage) - MsgLen, Spec, va);
    va_end(va);

    if (log != NULL)
    {
        fprintf(log, "FINAL_MESSAGE=%s\n", FinalMessage);
        fprintf(log, "--- EXIT UtAssert_Message ---\n\n");
        fflush(log);
        fclose(log);
    }

    UT_BSP_DoText(MessageType, FinalMessage);
}
