void UtAssert_Message(uint8 MessageType, const char *File, uint32 Line, const char *Spec, ...)
{
    va_list     va;
    char        FinalMessage[256];
    const char *BaseName;
    size_t      MsgLen;
    FILE       *log = fopen("/tmp/logs/873.txt", "a");

    /* Minimal entry log to show function was reached and important pointers */
    if (log != NULL)
    {
        fprintf(log, "UtAssert_Message ENTER: MessageType=%u, File=%p, Line=%u, Spec=%p\n",
                (unsigned)MessageType, (void *)File, (unsigned)Line, (const void *)Spec);
        fflush(log);
    }

    if (File != NULL)
    {
        /* Log that we are about to call strrchr (this is where a non-terminated string can cause overflow) */
        if (log != NULL)
        {
            fprintf(log, "UtAssert_Message: File is non-NULL, calling strrchr(File, '/')\n");
            fflush(log);
        }

        BaseName = strrchr(File, '/');

        if (log != NULL)
        {
            fprintf(log, "UtAssert_Message: strrchr returned %p\n", (void *)BaseName);
            fflush(log);
        }

        if (BaseName == NULL)
        {
            BaseName = File;
        }
        else
        {
            ++BaseName;
        }

        /* Compose the prefix "basename:line:" as in original code and log the intermediate result */
        snprintf(FinalMessage, sizeof(FinalMessage), "%s:%u:", BaseName, (unsigned int)Line);
        MsgLen = strlen(FinalMessage);

        if (log != NULL)
        {
            fprintf(log, "UtAssert_Message: BaseName=%p, Prefix=\"%s\", MsgLen=%zu\n",
                    (void *)BaseName, FinalMessage, MsgLen);
            fflush(log);
        }
    }
    else
    {
        MsgLen = 0;
        if (log != NULL)
        {
            fprintf(log, "UtAssert_Message: File is NULL, skipping basename processing\n");
            fflush(log);
        }
    }

    va_start(va, Spec);
    vsnprintf(&FinalMessage[MsgLen], sizeof(FinalMessage) - MsgLen, Spec, va);
    va_end(va);

    if (log != NULL)
    {
        /* Log the final composed message (truncated to buffer) to help reproduce what was emitted */
        fprintf(log, "UtAssert_Message: FinalMessage (len %zu): %s\n", strlen(FinalMessage), FinalMessage);
        fflush(log);
    }

    UT_BSP_DoText(MessageType, FinalMessage);

    if (log != NULL)
    {
        fprintf(log, "UtAssert_Message EXIT\n");
        fclose(log);
    }
}
