void UtAssert_Message(uint8 MessageType, const char *File, uint32 Line, const char *Spec, ...)
{
    va_list     va;
    char        FinalMessage[256];
    const char *BaseName = NULL;
    const char *LogBaseName = "(null)";
    size_t      MsgLen;
    FILE       *logf = NULL;

    if (File != NULL)
    {
        BaseName = strrchr(File, '/');
        if (BaseName == NULL)
        {
            BaseName = File;
        }
        else
        {
            ++BaseName;
        }
        LogBaseName = BaseName;
        snprintf(FinalMessage, sizeof(FinalMessage), "%s:%u:", BaseName, (unsigned int)Line);
        MsgLen = strlen(FinalMessage);
    }
    else
    {
        MsgLen = 0;
    }

    va_start(va, Spec);
    vsnprintf(&FinalMessage[MsgLen], sizeof(FinalMessage) - MsgLen, Spec, va);
    va_end(va);

    /* Open the log file and append a record describing the call and state.
       This helps reproduce scenarios where other code (e.g. memset calls)
       might operate on NULL or undersized buffers. */
    logf = fopen("/tmp/logs/873.txt", "a");
    if (logf != NULL)
    {
        /* Guard printing of potentially NULL pointers */
        const char *specstr = (Spec != NULL) ? Spec : "(null)";
        /* Write a compact, single-line log entry with the key state */
        fprintf(logf, "UtAssert_Message: MessageType=%u File=%s Line=%u BaseName=%s MsgLen=%zu Spec=%s FinalMessage=%s\n",
                (unsigned int)MessageType,
                (File != NULL) ? File : "(null)",
                (unsigned int)Line,
                LogBaseName,
                MsgLen,
                specstr,
                FinalMessage);
        fclose(logf);
    }

    UT_BSP_DoText(MessageType, FinalMessage);
}
