bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;
    bool        used_strlen1 = false;
    bool        used_strlen2 = false;

    /* Open log for tracing control flow/state for MISRA rule investigation */
    FILE *log = fopen("/tmp/logs/3354.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER UtAssert_StringBufCompare: String1=%p, String1Max=%zu, String2=%p, String2Max=%zu, CompareType=%d, File=%s, Line=%u\n",
                (const void *)String1, String1Max, (const void *)String2, String2Max, (int)CompareType,
                (File != NULL) ? File : "(null)", (unsigned int)Line);
    }

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
        if (log) fprintf(log, "String1 is NULL\n");
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
        used_strlen1 = true;
        if (log) fprintf(log, "String1: used strlen(), computed EndPtr1=%p, strlen=%zu\n", (const void *)EndPtr1, strlen(String1));
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
        if (log) fprintf(log, "String1: used memchr(), EndPtr1=%p\n", (const void *)EndPtr1);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }
    if (log) fprintf(log, "After String1 end detection: FormatLen1=%zu\n", FormatLen1);

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
        if (log) fprintf(log, "String2 is NULL\n");
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
        used_strlen2 = true;
        if (log) fprintf(log, "String2: used strlen(), computed EndPtr2=%p, strlen=%zu\n", (const void *)EndPtr2, strlen(String2));
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
        if (log) fprintf(log, "String2: used memchr(), EndPtr2=%p\n", (const void *)EndPtr2);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }
    if (log) fprintf(log, "After String2 end detection: FormatLen2=%zu\n", FormatLen2);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        if (log) fprintf(log, "Both strings empty -> Compare=0\n");
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        size_t compare_len;

        if (FormatLen1 < FormatLen2)
        {
            compare_len = FormatLen1;
            if (log) fprintf(log, "Choosing to compare first %zu bytes (FormatLen1 < FormatLen2). used_strlen1=%d used_strlen2=%d\n",
                             compare_len, used_strlen1 ? 1 : 0, used_strlen2 ? 1 : 0);
            Compare = memcmp(String1, String2, compare_len);
        }
        else
        {
            compare_len = FormatLen2;
            if (log) fprintf(log, "Choosing to compare first %zu bytes (FormatLen2 <= FormatLen1). used_strlen1=%d used_strlen2=%d\n",
                             compare_len, used_strlen1 ? 1 : 0, used_strlen2 ? 1 : 0);
            Compare = memcmp(String1, String2, compare_len);
        }

        if (log) fprintf(log, "memcmp returned %d\n", Compare);

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            int oldCompare = Compare;
            Compare = (int)(FormatLen1 - FormatLen2);
            if (log) fprintf(log, "Initial content equal (memcmp==%d). Adjusted Compare = FormatLen1 - FormatLen2 = %d\n", oldCompare, Compare);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    if (log) fprintf(log, "After comparison: Compare=%d, Result=%d\n", Compare, Result ? 1 : 0);

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            if (log) fprintf(log, "Truncated String1 at newline, new FormatLen1=%zu\n", FormatLen1);
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            if (log) fprintf(log, "Truncated String2 at newline, new FormatLen2=%zu\n", FormatLen2);
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    if (log) fprintf(log, "Returning Result=%d, ScrubbedString1='%s', ScrubbedString2='%s'\n", Result ? 1 : 0, ScrubbedString1, ScrubbedString2);
    if (log) fclose(log);

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
