bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Open logging file to capture control flow and state for debugging the potential violation */
    FILE *logfp = fopen("/tmp/logs/3353.txt", "a");
    if (logfp != NULL)
    {
        fprintf(logfp, "Entered UtAssert_StringBufCompare: File=%s Line=%u\n", File ? File : "(null)", (unsigned)Line);
        fprintf(logfp, "Inputs: String1=%p String1Max=%zu String2=%p String2Max=%zu CompareType=%d\n",
                (const void *)String1, String1Max, (const void *)String2, String2Max, (int)CompareType);
    }

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
        if (logfp != NULL) fprintf(logfp, "Branch: String1 == NULL -> EndPtr1 = NULL\n");
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        if (logfp != NULL) fprintf(logfp, "Branch: String1Max == UTASSERT_STRINGBUF_NULL_TERM -> use strlen()\n");
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
    }
    else
    {
        if (logfp != NULL) fprintf(logfp, "Branch: memchr(String1, 0, String1Max)\n");
        EndPtr1 = memchr(String1, 0, String1Max);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
        if (logfp != NULL) fprintf(logfp, "EndPtr1 != NULL -> FormatLen1 = %zu (EndPtr1=%p)\n", FormatLen1, (const void *)EndPtr1);
    }
    else
    {
        FormatLen1 = String1Max;
        if (logfp != NULL) fprintf(logfp, "EndPtr1 == NULL -> FormatLen1 = String1Max = %zu\n", FormatLen1);
    }

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
        if (logfp != NULL) fprintf(logfp, "Branch: String2 == NULL -> EndPtr2 = NULL\n");
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        if (logfp != NULL) fprintf(logfp, "Branch: String2Max == UTASSERT_STRINGBUF_NULL_TERM -> use strlen()\n");
        EndPtr2 = String2 + strlen(String2);
    }
    else
    {
        if (logfp != NULL) fprintf(logfp, "Branch: memchr(String2, 0, String2Max)\n");
        EndPtr2 = memchr(String2, 0, String2Max);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
        if (logfp != NULL) fprintf(logfp, "EndPtr2 != NULL -> FormatLen2 = %zu (EndPtr2=%p)\n", FormatLen2, (const void *)EndPtr2);
    }
    else
    {
        FormatLen2 = String2Max;
        if (logfp != NULL) fprintf(logfp, "EndPtr2 == NULL -> FormatLen2 = String2Max = %zu\n", FormatLen2);
    }

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        if (logfp != NULL) fprintf(logfp, "Both FormatLen1 and FormatLen2 are 0 -> Compare = 0\n");
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            if (logfp != NULL) fprintf(logfp, "Comparing memcmp(String1, String2, %zu)\n", FormatLen1);
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            if (logfp != NULL) fprintf(logfp, "Comparing memcmp(String1, String2, %zu)\n", FormatLen2);
            Compare = memcmp(String1, String2, FormatLen2);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = FormatLen1 - FormatLen2;
            if (logfp != NULL) fprintf(logfp, "memcmp equal -> Compare = FormatLen1 - FormatLen2 = %d\n", Compare);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than or equal to reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        if (logfp != NULL) fprintf(logfp, "About to search for newline in String1: memchr(String1, '\\n', %zu) String1=%p\n",
                                   FormatLen1, (const void *)String1);
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (logfp != NULL) fprintf(logfp, "Result of memchr for newline on String1: EndPtr1=%p\n", (const void *)EndPtr1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            if (logfp != NULL) fprintf(logfp, "Newline found in String1 -> new FormatLen1=%zu\n", FormatLen1);
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
        if (logfp != NULL) fprintf(logfp, "Copied %zu bytes into ScrubbedString1\n", FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        if (logfp != NULL) fprintf(logfp, "About to search for newline in String2: memchr(String2, '\\n', %zu) String2=%p\n",
                                   FormatLen2, (const void *)String2);
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (logfp != NULL) fprintf(logfp, "Result of memchr for newline on String2: EndPtr2=%p\n", (const void *)EndPtr2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            if (logfp != NULL) fprintf(logfp, "Newline found in String2 -> new FormatLen2=%zu\n", FormatLen2);
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
        if (logfp != NULL) fprintf(logfp, "Copied %zu bytes into ScrubbedString2\n", FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    if (logfp != NULL)
    {
        fprintf(logfp, "Final result: Result=%d ScrubbedString1='%s' ScrubbedString2='%s'\n\n",
                (int)Result, ScrubbedString1, ScrubbedString2);
        fclose(logfp);
    }

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
