bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Open log file for tracing control flow and state */
    {
        FILE *log = fopen("/tmp/logs/3353.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "UtAssert_StringBufCompare ENTER. File=%s Line=%u\n",
                    (File != NULL) ? File : "(null)", (unsigned)Line);
            fprintf(log, "  Params: String1=%p String1Max=%zu String2=%p String2Max=%zu CompareType=%d\n",
                    (const void *)String1, String1Max, (const void *)String2, String2Max, (int)CompareType);
            fclose(log);
        }
    }

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  String1 is NULL -> EndPtr1 = NULL\n");
                fclose(log);
            }
        }
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  String1Max indicates null-terminated mode -> using strlen(String1)\n");
                fclose(log);
            }
        }
        EndPtr1 = String1 + strlen(String1);
    }
    else
    {
        /* Log the memchr call parameters immediately before invoking memchr */
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  Calling memchr(String1, 0, String1Max=%zu) at address %p\n",
                        String1Max, (const void *)String1);
                fclose(log);
            }
        }
        EndPtr1 = memchr(String1, 0, String1Max);
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  memchr(String1,0,...) returned %p\n", (const void *)EndPtr1);
                fclose(log);
            }
        }
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }

    {
        FILE *log = fopen("/tmp/logs/3353.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  Computed FormatLen1 = %zu\n", FormatLen1);
            fclose(log);
        }
    }

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  String2 is NULL -> EndPtr2 = NULL\n");
                fclose(log);
            }
        }
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  String2Max indicates null-terminated mode -> using strlen(String2)\n");
                fclose(log);
            }
        }
    }
    else
    {
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  Calling memchr(String2, 0, String2Max=%zu) at address %p\n",
                        String2Max, (const void *)String2);
                fclose(log);
            }
        }
        EndPtr2 = memchr(String2, 0, String2Max);
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  memchr(String2,0,...) returned %p\n", (const void *)EndPtr2);
                fclose(log);
            }
        }
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }

    {
        FILE *log = fopen("/tmp/logs/3353.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  Computed FormatLen2 = %zu\n", FormatLen2);
            fclose(log);
        }
    }

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  Both FormatLen1 and FormatLen2 are 0 -> Compare = 0\n");
                fclose(log);
            }
        }
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            {
                FILE *log = fopen("/tmp/logs/3353.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  Comparing memcmp(String1, String2, %zu) because FormatLen1 < FormatLen2\n",
                            FormatLen1);
                    fclose(log);
                }
            }
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            {
                FILE *log = fopen("/tmp/logs/3353.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  Comparing memcmp(String1, String2, %zu) because FormatLen1 >= FormatLen2\n",
                            FormatLen2);
                    fclose(log);
                }
            }
            Compare = memcmp(String1, String2, FormatLen2);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = FormatLen1 - FormatLen2;
            {
                FILE *log = fopen("/tmp/logs/3353.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  Initial memcmp equal -> Compare set to (FormatLen1 - FormatLen2) = %d\n", Compare);
                    fclose(log);
                }
            }
        }
        else
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  memcmp returned %d (non-zero)\n", Compare);
                fclose(log);
            }
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    {
        FILE *log = fopen("/tmp/logs/3353.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  Final Compare=%d Result=%d -> preparing scrubbed copies\n", Compare, Result);
            fclose(log);
        }
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  Checking for newline in String1 within first %zu bytes\n", FormatLen1);
                fclose(log);
            }
        }
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            {
                FILE *log = fopen("/tmp/logs/3353.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  Newline found in String1 -> truncated FormatLen1 = %zu\n", FormatLen1);
                    fclose(log);
                }
            }
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        {
            FILE *log = fopen("/tmp/logs/3353.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "  Checking for newline in String2 within first %zu bytes\n", FormatLen2);
                fclose(log);
            }
        }
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            {
                FILE *log = fopen("/tmp/logs/3353.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  Newline found in String2 -> truncated FormatLen2 = %zu\n", FormatLen2);
                    fclose(log);
                }
            }
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    {
        FILE *log = fopen("/tmp/logs/3353.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "  ScrubbedString1='%s' ScrubbedString2='%s'\n", ScrubbedString1, ScrubbedString2);
            fprintf(log, "UtAssert_StringBufCompare EXIT. File=%s Line=%u\n\n",
                    (File != NULL) ? File : "(null)", (unsigned)Line);
            fclose(log);
        }
    }

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: \'%s\' == \'%s\'", ScrubbedString1,
                      ScrubbedString2);
}
