bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Open log file (attempt primary location, fallback if not writable) */
    FILE *logf = fopen("/tmp/logs/3353.txt", "a");
    if (logf == NULL)
    {
        logf = fopen("/tmp/3353.txt", "a");
    }
    if (logf != NULL)
    {
        fprintf(logf, "Entered UtAssert_StringBufCompare: String1=%p String1Max=%zu String2=%p String2Max=%zu File=%p Line=%u\n",
                (const void *)String1, String1Max, (const void *)String2, String2Max, (const void *)File, (unsigned)Line);
    }

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
        if (logf) fprintf(logf, "String1 is NULL -> EndPtr1=NULL\n");
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
        if (logf) fprintf(logf, "String1Max indicates NULL_TERM; strlen(String1)=%zu -> EndPtr1=%p\n",
                         strlen(String1), (const void *)EndPtr1);
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
        if (logf) fprintf(logf, "memchr(String1,0,String1Max) returned %p\n", (const void *)EndPtr1);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }
    if (logf) fprintf(logf, "Computed FormatLen1=%zu\n", FormatLen1);

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
        if (logf) fprintf(logf, "String2 is NULL -> EndPtr2=NULL\n");
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
        if (logf) fprintf(logf, "String2Max indicates NULL_TERM; strlen(String2)=%zu -> EndPtr2=%p\n",
                         strlen(String2), (const void *)EndPtr2);
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
        if (logf) fprintf(logf, "memchr(String2,0,String2Max) returned %p\n", (const void *)EndPtr2);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }
    if (logf) fprintf(logf, "Computed FormatLen2=%zu\n", FormatLen2);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        if (logf) fprintf(logf, "Both formats empty -> Compare=0\n");
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            Compare = memcmp(String1, String2, FormatLen1);
            if (logf) fprintf(logf, "memcmp with len=%zu returned %d (FormatLen1 < FormatLen2)\n", FormatLen1, Compare);
        }
        else
        {
            Compare = memcmp(String1, String2, FormatLen2);
            if (logf) fprintf(logf, "memcmp with len=%zu returned %d (FormatLen1 >= FormatLen2)\n", FormatLen2, Compare);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = (int)(FormatLen1 - FormatLen2);
            if (logf) fprintf(logf, "Content equal; Compare set to FormatLen1-FormatLen2 = %d\n", Compare);
        }
    }

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            if (logf) fprintf(logf, "CompareType EQ -> Result=%d\n", (int)Result);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            if (logf) fprintf(logf, "CompareType NEQ -> Result=%d\n", (int)Result);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            if (logf) fprintf(logf, "CompareType LT -> Result=%d\n", (int)Result);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            if (logf) fprintf(logf, "CompareType GT -> Result=%d\n", (int)Result);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            if (logf) fprintf(logf, "CompareType LTEQ -> Result=%d\n", (int)Result);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            if (logf) fprintf(logf, "CompareType GTEQ -> Result=%d\n", (int)Result);
            break;
        default: /* should never happen */
            Result = false;
            if (logf) fprintf(logf, "CompareType UNKNOWN -> Result=false\n");
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (logf) fprintf(logf, "Searching for newline in String1 with len=%zu -> memchr returned %p\n", FormatLen1, (const void *)EndPtr1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            if (logf) fprintf(logf, "Newline found in String1 -> new FormatLen1=%zu\n", FormatLen1);
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    if (logf) fprintf(logf, "About to terminate ScrubbedString1 at index FormatLen1=%zu (buffer size=%zu)\n", FormatLen1, (size_t)sizeof(ScrubbedString1));
    if (FormatLen1 >= sizeof(ScrubbedString1))
    {
        if (logf) fprintf(logf, "WARNING: FormatLen1 (%zu) >= ScrubbedString1 size (%zu) - potential out-of-bounds write!\n",
                         FormatLen1, (size_t)sizeof(ScrubbedString1));
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (logf) fprintf(logf, "Searching for newline in String2 with len=%zu -> memchr returned %p\n", FormatLen2, (const void *)EndPtr2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            if (logf) fprintf(logf, "Newline found in String2 -> new FormatLen2=%zu\n", FormatLen2);
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    if (logf) fprintf(logf, "About to terminate ScrubbedString2 at index FormatLen2=%zu (buffer size=%zu)\n", FormatLen2, (size_t)sizeof(ScrubbedString2));
    if (FormatLen2 >= sizeof(ScrubbedString2))
    {
        if (logf) fprintf(logf, "WARNING: FormatLen2 (%zu) >= ScrubbedString2 size (%zu) - potential out-of-bounds write!\n",
                         FormatLen2, (size_t)sizeof(ScrubbedString2));
    }
    ScrubbedString2[FormatLen2] = 0;

    if (logf)
    {
        fprintf(logf, "Final: ScrubbedString1_len=%zu ScrubbedString2_len=%zu Result=%d Compare=%d\n\n",
                FormatLen1, FormatLen2, (int)Result, Compare);
        fclose(logf);
    }

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
